      const { analyzeSubscriptionPatterns } = await import('./openai');
      const detectedSubscriptions = await analyzeSubscriptionPatterns(transactions);

      // Auto-criar assinaturas detectadas com alta confian√ßa
      const createdSubscriptions = [];
      for (const detected of detectedSubscriptions) {
        if (detected.confidence > 0.8) {
          try {
            // Verificar se j√° existe
            const existing = await storage.getSubscriptionsByUser(userId);
            const exists = existing.some(sub => 
              sub.merchant.toLowerCase().includes(detected.merchant.toLowerCase())
            );

            if (!exists) {
              const nextMonth = new Date();
              nextMonth.setMonth(nextMonth.getMonth() + 1);

              const subscriptionData = insertSubscriptionSchema.parse({
                userId,
                merchant: detected.merchant,
                amount: detected.amount.toString(),
                frequency: 'monthly',
                status: 'active',
                nextChargeDate: nextMonth,
                categoryId: null,
                confirmedByUser: false,
              });

              const subscription = await storage.createSubscription(subscriptionData);
              createdSubscriptions.push(subscription);
              console.log(`Auto-criou assinatura: ${detected.merchant}`);
            }
          } catch (createError) {
            console.error(`Erro ao criar assinatura para ${detected.merchant}:`, createError);
          }
        }
      }

      res.json({
        detectedSubscriptions,
        createdSubscriptions,
        message: `IA analisou ${transactions.length} transa√ß√µes e detectou ${detectedSubscriptions.length} poss√≠veis assinaturas. ${createdSubscriptions.length} foram criadas automaticamente.`
      });
    } catch (error) {
      console.error('Error analyzing subscriptions with AI:', error);
      res.status(500).json({ message: 'Failed to analyze subscriptions' });
    }
  });

  // Goals Management Routes
  app.get('/api/goals', isAuthenticated, async (req: any, res) => {
    try {
      const userId = getUserId(req);
      const { organizationId, familyGroupId } = req.query;

      const goals = await storage.getGoalsByUser(userId, organizationId, familyGroupId);
      res.json(goals);
    } catch (error) {
      console.error('Error fetching goals:', error);
      res.status(500).json({ message: 'Failed to fetch goals' });
    }
  });

  app.post('/api/goals', isAuthenticated, async (req: any, res) => {
    try {
      const userId = getUserId(req);
      const validatedData = insertGoalSchema.parse({ ...req.body, userId });

      const goal = await storage.createGoal(validatedData);
      res.status(201).json(goal);
    } catch (error) {
      console.error('Error creating goal:', error);
      res.status(500).json({ message: 'Failed to create goal' });
    }
  });

  // Nexo Predictive Analytics Routes
  app.get('/api/nexo/cashflow-prediction', isAuthenticated, async (req: any, res) => {
    try {
      const userId = getUserId(req);
      const { days = 30 } = req.query;

      // Simple cashflow prediction based on recent transactions
      const recentTransactions = await storage.getRecentTransactions(userId, 90);

      // Group by income and expenses
      const income = recentTransactions.filter(t => t.type === 'income');
      const expenses = recentTransactions.filter(t => t.type === 'expense');

      // Calculate averages
      const avgMonthlyIncome = income.reduce((sum, t) => sum + parseFloat(t.amount), 0) / 3;
      const avgMonthlyExpenses = expenses.reduce((sum, t) => sum + parseFloat(t.amount), 0) / 3;

      const predictedIncome = avgMonthlyIncome * (parseInt(days as string) / 30);
      const predictedExpenses = avgMonthlyExpenses * (parseInt(days as string) / 30);
      const netCashflow = predictedIncome - predictedExpenses;

      const currentBalance = await storage.getTotalBalance(userId);
      const projectedBalance = currentBalance + netCashflow;

      res.json({
        period: `${days} days`,
        currentBalance,
        predictedIncome,
        predictedExpenses,
        netCashflow,
        projectedBalance,
        risk: projectedBalance < 0 ? 'high' : projectedBalance < currentBalance * 0.1 ? 'medium' : 'low',
        recommendations: [
          projectedBalance < 0 && "‚ö†Ô∏è Predicted negative balance - consider reducing expenses",
          netCashflow < 0 && "üìâ Negative cashflow predicted - review recurring expenses",
          projectedBalance > currentBalance * 1.1 && "üí∞ Positive outlook - consider increasing investments"
        ].filter(Boolean)
      });
    } catch (error) {
      console.error('Error generating cashflow prediction:', error);
      res.status(500).json({ message: 'Failed to generate cashflow prediction' });
    }
  });

  app.post('/api/nexo/scenario-simulation', isAuthenticated, async (req: any, res) => {
    try {
      const userId = getUserId(req);
      const { incomeChange, expenseChanges, newExpenses } = req.body;

      const currentBalance = await storage.getTotalBalance(userId);
      const recentTransactions = await storage.getRecentTransactions(userId, 90);

      // Calculate baseline
      const currentIncome = recentTransactions
        .filter(t => t.type === 'income')
        .reduce((sum, t) => sum + parseFloat(t.amount), 0) / 3; // Monthly average

      const currentExpenses = recentTransactions
        .filter(t => t.type === 'expense')
        .reduce((sum, t) => sum + parseFloat(t.amount), 0) / 3;

      // Apply changes
      const newIncome = currentIncome * (1 + (incomeChange || 0) / 100);
      const modifiedExpenses = currentExpenses * (1 + (expenseChanges || 0) / 100);
      const additionalExpenses = (newExpenses || []).reduce((sum: number, exp: any) => sum + exp.amount, 0);

      const totalNewExpenses = modifiedExpenses + additionalExpenses;
      const newNetCashflow = newIncome - totalNewExpenses;

      res.json({
        scenario: 'Custom Simulation',
        baseline: {
          income: currentIncome,
          expenses: currentExpenses,
          netCashflow: currentIncome - currentExpenses,
          balance: currentBalance
        },
        simulation: {
          income: newIncome,
          expenses: totalNewExpenses,
          netCashflow: newNetCashflow,
          projectedBalance: currentBalance + newNetCashflow
        },
        changes: {
          incomeChange: newIncome - currentIncome,
          expenseChange: totalNewExpenses - currentExpenses,
          netChange: newNetCashflow - (currentIncome - currentExpenses)
        },
        recommendation: newNetCashflow > 0 ? 
          'Positive scenario - good financial outlook' : 
          'Negative scenario - consider adjusting expenses'
      });
    } catch (error) {
      console.error('Error running scenario simulation:', error);
      res.status(500).json({ message: 'Failed to run scenario simulation' });
    }
  });

  // Educational Content Routes (Academy)
  app.get('/api/academy/content', isAuthenticated, async (req: any, res) => {
    try {
      const userId = getUserId(req);
      const { category } = req.query;

      const content = await storage.getEducationalContentForUser(userId, category);
      res.json(content);
    } catch (error) {
      console.error('Error fetching educational content:', error);
      res.status(500).json({ message: 'Failed to fetch educational content' });
    }
  });

  // ü§ñ FUN√á√ÉO GEMINI DIRETA (API REST) - FUNCIONAMENTO GARANTIDO
  async function extractWithGemini(extractText: string, availableCategories: any[]): Promise<any[]> {
    try {
      const categories = availableCategories.map(c => c.name).join(', ');

      const prompt = `Extraia TODAS as transa√ß√µes banc√°rias do extrato brasileiro.

CATEGORIAS: ${categories}

REGRAS:
- Encontre padr√µes: PIX, TED, DOC, d√©bito, cr√©dito, pagamento, compra
- Identifique datas (DD/MM/AAAA)
- Extraia valores (+ receitas, - despesas)
- Categorize automaticamente

EXTRATO:
${extractText.substring(0, 8000)}

RESPONDA APENAS JSON:
{"transactions":[{"date":"AAAA-MM-DD","description":"DESC","amount":VALOR,"type":"income/expense","category":"CATEGORIA","confidence":0.9}]}`;

      console.log(`[Gemini] Enviando prompt de ${prompt.length} caracteres`);

      const response = await fetch(`https://generativelanguage.googleapis.com/v1beta/models/gemini-2.5-flash:generateContent?key=${process.env.GEMINI_API_KEY}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          contents: [{
            parts: [{ text: prompt }]
          }],
          generationConfig: {
            temperature: 0.1,
            maxOutputTokens: 4000,
          }
        })
      });

      if (!response.ok) {
        throw new Error(`API Gemini erro: ${response.status} ${response.statusText}`);
      }

      const data = await response.json();
      console.log(`[Gemini] Status da resposta: ${response.status}`);

      const text = data.candidates?.[0]?.content?.parts?.[0]?.text || '';
      console.log(`[Gemini] Texto recebido: ${text.length} caracteres`);

      if (!text) {
        console.log(`[Gemini] ‚ö†Ô∏è  Resposta vazia da API`);
        return await extractWithRegexFallback(extractText);
      }

      // Parse JSON robusto
      const jsonMatch = text.match(/\{[\s\S]*\}/);
      if (!jsonMatch) {
        console.log(`[Gemini] Nenhum JSON encontrado, usando fallback regex`);
        return await extractWithRegexFallback(extractText);
      }

      try {
        const parsed = JSON.parse(jsonMatch[0]);
        const transactions = parsed.transactions || [];

        console.log(`[Gemini] ‚úÖ ${transactions.length} transa√ß√µes extra√≠das`);
        return transactions.map((t: any) => ({
          date: t.date || new Date().toISOString().split('T')[0],
          description: t.description || 'Transa√ß√£o',
          amount: parseFloat(t.amount) || 0,
          type: (t.type || 'expense').toLowerCase(),
          category: t.category || 'Outros',
          confidence: t.confidence || 0.8,
          reasoning: 'Categoriza√ß√£o Gemini'
        }));
      } catch (parseError) {
        console.log(`[Gemini] Erro no JSON parse, usando fallback regex`);
        return await extractWithRegexFallback(extractText);
      }

    } catch (error) {
      console.error(`[Gemini] Erro na extra√ß√£o:`, error);
      return await extractWithRegexFallback(extractText);
    }
  }

  // üöÄ EXTRATOR BANC√ÅRIO BRASILEIRO ULTRA-ROBUSTO - TODAS AS TRANSA√á√ïES
  async function extractWithRegexFallback(extractText: string): Promise<any[]> {
    console.log(`[Extrator] Processando ${extractText.length} caracteres - EXTRA√á√ÉO COMPLETA`);

    const transactions: any[] = [];
    const lines = extractText.split('\n');

    // Log de amostra para debug
    console.log(`[Extrator] Total de linhas: ${lines.length}`);
    console.log(`[Extrator] Primeiras 10 linhas:`);
    lines.slice(0, 10).forEach((line, i) => {
      console.log(`  ${i+1}: "${line.substring(0, 80)}"`);
    });

    let processedLines = 0;
    let foundTransactions = 0;

    for (let i = 0; i < lines.length; i++) {
      const line = lines[i].trim();
      if (!line || line.length < 5) continue;

      processedLines++;

      // FILTRAR CABE√áALHOS E METADADOS ANTES DE PROCESSAR
      const isHeader = line.toLowerCase().includes('sicredi') ||
                      line.toLowerCase().includes('associado:') ||
                      line.toLowerCase().includes('cooperativa:') ||
                      line.toLowerCase().includes('conta:') ||
                      line.toLowerCase().includes('extrato') ||
                      line.toLowerCase().includes('per√≠odo') ||
                      line.toLowerCase().includes('data') ||
                      line.toLowerCase().includes('descri√ß√£o') ||
                      line.toLowerCase().includes('saldo anterior') ||
                      line.match(/^\d{4}$/) || // Apenas n√∫meros como "0715"
                      line.match(/^\d{5}-\d$/) || // Formato conta "47413-6"
                      line.length < 10; // Linhas muito curtas

      if (isHeader) continue;

      // ESTRAT√âGIA 1: Buscar qualquer coisa que pare√ßa valor monet√°rio
      const monetaryMatches = line.match(/([\d]{1,3}(?:\.[\d]{3})*,[\d]{2}|[\d]{2,}(?:,[\d]{2})?)/g);

      if (monetaryMatches) {
        for (const match of monetaryMatches) {
          // Converter para n√∫mero
          let amount = 0;
          let cleanValue = match.replace(/[^\d,.-]/g, '');

          if (cleanValue.includes('.') && cleanValue.includes(',')) {
            // Formato brasileiro: 1.234,56
            amount = parseFloat(cleanValue.replace(/\./g, '').replace(',', '.'));
          } else if (cleanValue.includes(',')) {
            // Formato: 1234,56
            amount = parseFloat(cleanValue.replace(',', '.'));
          } else {
            // S√≥ n√∫meros: 1234
            amount = parseFloat(cleanValue);
          }

          // Se √© um valor v√°lido (entre R$ 1 e R$ 50.000)
          if (amount >= 1 && amount <= 50000) {

            // Extrair data da linha (se houver)
            const dateMatch = line.match(/(\d{1,2}[\/\-]\d{1,2}[\/\-](?:\d{4}|\d{2}))/);
            const dateStr = dateMatch ? dateMatch[1] : '';

            // Extrair descri√ß√£o (tudo que n√£o for n√∫mero ou data)
            let description = line
              .replace(/[\d]{1,3}(?:\.[\d]{3})*,[\d]{2}/g, '') // Remove valores
              .replace(/[\d]{2,}/g, '') // Remove n√∫meros grandes
              .replace(/\d{1,2}[\/\-]\d{1,2}[\/\-](?:\d{4}|\d{2})/g, '') // Remove datas
              .replace(/\s+/g, ' ') // Normaliza espa√ßos
              .trim();

            // Se sobrou descri√ß√£o v√°lida
            if (description.length >= 3 && description.length <= 100) {

              // Determinar tipo da transa√ß√£o
              const isCredit = line.toLowerCase().includes('cr√©d') || 
                              line.toLowerCase().includes('receita') ||
                              line.toLowerCase().includes('deposito') ||
                              line.toLowerCase().includes('+');

              const isDebit = line.toLowerCase().includes('d√©b') || 
                             line.toLowerCase().includes('pix') ||
                             line.toLowerCase().includes('ted') ||
                             line.toLowerCase().includes('doc') ||
                             line.toLowerCase().includes('compra') ||
                             line.toLowerCase().includes('pagamento') ||
                             line.toLowerCase().includes('saque') ||
                             line.toLowerCase().includes('-');

              // Se n√£o conseguir determinar, assume d√©bito (mais comum)
              const finalIsCredit = isCredit && !isDebit;

              transactions.push({
                date: normalizeDate(dateStr) || new Date().toISOString().split('T')[0],
                description: description.substring(0, 80),
                amount: finalIsCredit ? Math.abs(amount) : -Math.abs(amount),
                type: finalIsCredit ? 'income' : 'expense',
                category: 'Outros',
                confidence: 0.7,
                reasoning: `Extra√ß√£o de valor monet√°rio`
              });

              foundTransactions++;
            }
          }
        }
      }
    }

    console.log(`[Extrator] Processadas ${processedLines} linhas, encontradas ${foundTransactions} transa√ß√µes`);

    // Filtros MUITO mais flex√≠veis - preservar m√°ximo de transa√ß√µes
    const uniqueTransactions = transactions.filter((transaction, index, self) => {
      // Apenas filtrar valores EXTREMAMENTE inv√°lidos
      if (Math.abs(transaction.amount) < 0.50 || Math.abs(transaction.amount) > 500000) return false;

      // Filtrar apenas strings OBVIAMENTE inv√°lidas
      const desc = transaction.description.toLowerCase().trim();
      if (desc.length < 2 || desc === 'data' || desc === 'descri√ß√£o') return false;

      // Remover duplicatas apenas se forem TOTALMENTE id√™nticas
      return index === self.findIndex(t => 
        Math.abs(t.amount - transaction.amount) < 0.01 &&
        t.description.substring(0, 25) === transaction.description.substring(0, 25) &&
        t.date === transaction.date
      );
    });

    console.log(`[Extrator] ${transactions.length} brutas ‚Üí ${uniqueTransactions.length} √∫nicas`);

    // Log das primeiras transa√ß√µes para verifica√ß√£o
    console.log(`[Extrator] Primeiras 5 transa√ß√µes V√ÅLIDAS:`);
    uniqueTransactions.slice(0, 5).forEach((t, i) => {
      console.log(`  ${i+1}. ${t.date} | ${t.description.substring(0, 40)} | R$ ${t.amount.toFixed(2)}`);
    });

    return uniqueTransactions;
  }

  function normalizeDate(dateStr: string): string {
    try {
      if (dateStr.includes('/')) {
        const [day, month, year] = dateStr.split('/');
        return `${year || '2025'}-${month.padStart(2, '0')}-${day.padStart(2, '0')}`;
      }
      return dateStr;
    } catch {
      return new Date().toISOString().split('T')[0];
    }
  }

  const httpServer = createServer(app);
  return httpServer;
}

