  getFinancialSummary(userId: string, startDate: Date, endDate: Date): Promise<{
    totalIncome: string;
    totalExpenses: string;
    balance: string;
    transactionCount: number;
    categoryBreakdown: Array<{ categoryName: string; total: string; count: number; }>;
  }>;

