  // Transaction operations
  async getTransactions(userId: string, filters: {
    categoryId?: string;
    startDate?: Date;
    endDate?: Date;
    type?: 'income' | 'expense';
    paymentMethod?: string;
    search?: string;
    limit?: number;
    offset?: number;
  } = {}): Promise<{ transactions: Transaction[]; totalCount: number }> {
    const {
      categoryId,
      startDate,
      endDate,
      type,
      paymentMethod,
      search,
      limit = 50,
      offset = 0
    } = filters;

    const filter = await this.getDataFilter(userId);
    const conditions = [eq(transactions.userId, userId)];

    // Apply data isolation based on account type
    if (filter.organizationId) {
      conditions.push(eq(transactions.organizationId, filter.organizationId));
    } else if (filter.familyGroupId) {
      conditions.push(eq(transactions.familyGroupId, filter.familyGroupId));
    } else {
      // Individual account - show transactions that don't belong to any organization or family
      // This includes old transactions that were created before the separation was implemented
      conditions.push(
        or(
          isNull(transactions.organizationId),
          eq(transactions.organizationId, '')
        )
      );
      conditions.push(
        or(
          isNull(transactions.familyGroupId),
          eq(transactions.familyGroupId, '')
        )
      );
    }

    if (categoryId) {
      conditions.push(eq(transactions.categoryId, categoryId));
    }
    if (startDate) {
      conditions.push(gte(transactions.date, startDate));
    }
    if (endDate) {
      conditions.push(lte(transactions.date, endDate));
    }
    if (type) {
      conditions.push(eq(transactions.type, type));
    }

    if (paymentMethod && paymentMethod !== 'all') {
      conditions.push(eq(transactions.paymentMethod, paymentMethod));
    }

    if (search && search.trim()) {
      const searchTerm = `%${search.toLowerCase()}%`;
      conditions.push(
        or(
          sql`LOWER(${transactions.description}) LIKE ${searchTerm}`,
          sql`LOWER(CAST(${transactions.paymentMethod} AS TEXT)) LIKE ${searchTerm}`
        )
      );
    }

    // Get total count with the same conditions
    const [countResult] = await db
      .select({ count: count() })
      .from(transactions)
      .where(and(...conditions));

    const totalCount = countResult?.count ?? 0;

    // Get paginated transactions
    const transactionsList = await db
      .select()
      .from(transactions)
      .where(and(...conditions))
      .orderBy(desc(transactions.date))
      .limit(limit)
      .offset(offset);

    return {
      transactions: transactionsList,
      totalCount
    };
  }

