@layer base {

  body {
    @apply bg-background text-foreground antialiased;
    font-family: var(--font-sans);
    line-height: 1.7;
    letter-spacing: -0.015em;
    text-rendering: optimizeLegibility;
    font-feature-settings: 'cv02', 'cv03', 'cv04', 'ss01';
    font-weight: 400;
    font-variation-settings: 'wght' 400;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
  }

  .dark body {
    @apply bg-background text-foreground;
    background-color: hsl(var(--background));
    color: hsl(var(--foreground));
  }

  /* Modern Typography Scale */
  h1, h2, h3, h4, h5, h6 {
    font-family: var(--font-display);
    font-weight: 600;
    letter-spacing: -0.025em;
    line-height: 1.25;
  }

  h1 { @apply text-4xl md:text-5xl font-bold; }
  h2 { @apply text-3xl md:text-4xl font-semibold; }
  h3 { @apply text-2xl md:text-3xl font-semibold; }
  h4 { @apply text-xl md:text-2xl font-medium; }
  h5 { @apply text-lg md:text-xl font-medium; }
  h6 { @apply text-base md:text-lg font-medium; }
}

@layer components {
  /* Modern Card Component */
  .modern-card {
    @apply bg-card rounded-2xl border border-border shadow-sm hover:shadow-lg;
    background-color: hsl(var(--card));
    border-color: hsl(var(--border));
    color: hsl(var(--card-foreground));
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .sidebar-item {
    @apply flex items-center px-4 py-3 text-gray-600 dark:text-gray-300 rounded-xl cursor-pointer relative min-h-[48px] transition-colors duration-200;
    width: 100%;
    white-space: nowrap;
  }

  .sidebar-item:hover {
    @apply bg-gray-50 dark:bg-gray-800/50;
    display: flex;
    align-items: center;
  }

  .sidebar-item.active {
    @apply bg-blue-500 text-white font-semibold;
  }


  .sidebar-icon {
    @apply flex-shrink-0 w-5 h-5;
    width: 20px;
    height: 20px;
    margin-right: 12px;
    display: flex;
    align-items: center;
    justify-content: center;
  }

  .sidebar-icon-active {
    @apply text-white;
  }

  .sidebar-item span {
    @apply text-sm font-medium flex-1;
    line-height: 1.4;
    display: flex;
    align-items: center;
    overflow: hidden;
    text-overflow: ellipsis;
    white-space: nowrap;
  }

  .financial-card {
    @apply bg-card dark:bg-card rounded-2xl p-6 shadow-sm hover:shadow-lg border border-border;
    background-color: hsl(var(--card));
    color: hsl(var(--card-foreground));
    border-color: hsl(var(--border));
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .gradient-card {
    @apply bg-gradient-to-br text-white rounded-2xl p-6 shadow-sm hover:shadow-lg;
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .chart-container {
    @apply bg-card dark:bg-card rounded-2xl p-6 shadow-sm hover:shadow-lg border border-border;
    background-color: hsl(var(--card));
    color: hsl(var(--card-foreground));
    border-color: hsl(var(--border));
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  }
}

@layer utilities {
  /* Force better dark mode visibility */
  .dark .text-gray-500 {
    @apply text-gray-300 !important;
  }

  .dark .text-gray-600 {
    @apply text-gray-200 !important;
  }

  .dark .bg-gray-100 {
    @apply bg-gray-700 !important;
  }

  .dark .bg-gray-200 {
    @apply bg-gray-600 !important;
  }

  .dark .border-gray-200 {
    @apply border-gray-600 !important;
  }

  .dark .border-gray-300 {
    @apply border-gray-500 !important;
  }

  /* Better progress bars */
  .dark [data-testid*="progress"] {
    @apply bg-gray-700 !important;
  }

  /* Better text contrast */
  .dark .text-muted {
    @apply text-gray-300 !important;
  }

  .text-success {
    color: hsl(var(--success));
  }

  .text-success-foreground {
    color: hsl(var(--success-foreground));
  }

  .bg-success {
    background-color: hsl(var(--success));
  }

  .bg-success-foreground {
    background-color: hsl(var(--success-foreground));
  }

  .text-warning {
    color: hsl(var(--warning));
