import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Skeleton } from "@/components/ui/skeleton";
import { Home, Zap, Wifi, Car, Calendar, Plus, Save, ChevronLeft, ChevronRight } from "lucide-react";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Checkbox } from "@/components/ui/checkbox";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { useState } from "react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { isUnauthorizedError } from "@/lib/authUtils";

const getCategoryIcon = (categoryName: string, description: string) => {
  const text = `${categoryName || ''} ${description}`.toLowerCase();
  if (text.includes('aluguel') || text.includes('casa') || text.includes('habita√ß√£o')) return Home;
  if (text.includes('energia') || text.includes('luz') || text.includes('el√©trica')) return Zap;
  if (text.includes('internet') || text.includes('wifi') || text.includes('telecom')) return Wifi;
  if (text.includes('seguro') || text.includes('carro') || text.includes('ve√≠culo')) return Car;
  return Calendar;
};

const getCategoryColor = (name: string) => {
  const lowerName = name.toLowerCase();
  if (lowerName.includes('aluguel')) return 'bg-blue-100 dark:bg-blue-900 text-blue-600';
  if (lowerName.includes('energia')) return 'bg-yellow-100 dark:bg-yellow-900 text-yellow-600';
  if (lowerName.includes('internet')) return 'bg-purple-100 dark:bg-purple-900 text-purple-600';
  if (lowerName.includes('seguro')) return 'bg-red-100 dark:bg-red-900 text-red-600';
  return 'bg-gray-100 dark:bg-gray-900 text-gray-600';
};

const getStatusColor = (isPaid: boolean) => {
  return isPaid ? 'bg-green-500' : 'bg-orange-500';
};

const getStatusTitle = (isPaid: boolean) => {
  return isPaid ? 'Pago' : 'Pendente';
};

const fixedExpenseSchema = z.object({
  name: z.string().min(1, "Nome √© obrigat√≥rio"),
  amount: z.string().min(1, "Valor √© obrigat√≥rio").refine((val) => !isNaN(Number(val)) && Number(val) > 0, "Valor deve ser um n√∫mero positivo"),
  dueDay: z.string().min(1, "Dia do vencimento √© obrigat√≥rio").refine((val) => {
    const day = parseInt(val);
    return day >= 1 && day <= 31;
  }, "Dia deve estar entre 1 e 31"),
  categoryId: z.string().optional(),
  isPaid: z.boolean().default(false),
  totalInstallments: z.string().optional(),
  paidInstallments: z.string().optional(),
});

type FixedExpenseFormData = z.infer<typeof fixedExpenseSchema>;

export function FixedExpenses() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [currentPage, setCurrentPage] = useState(0);

  const form = useForm<FixedExpenseFormData>({
    resolver: zodResolver(fixedExpenseSchema),
    defaultValues: {
      isPaid: false,
      totalInstallments: "",
      paidInstallments: "",
    },
  });

  const { data: expenses = [], isLoading } = useQuery({
    queryKey: ['/api/transactions/recurring'],
    queryFn: async () => {
      const response = await fetch('/api/transactions/recurring', {
        credentials: 'include',
      });
      if (!response.ok) throw new Error('Failed to fetch recurring transactions');
      return response.json();
    },
    staleTime: 0,
    refetchOnWindowFocus: true,
  });

  const { data: categories } = useQuery({
    queryKey: ['/api/categories'],
    queryFn: async () => {
      const response = await fetch('/api/categories', {
        credentials: 'include',
      });
      if (!response.ok) throw new Error('Failed to fetch categories');
      return response.json();
    },
  });

  const createFixedExpenseMutation = useMutation({
    mutationFn: async (data: FixedExpenseFormData) => {
      if (data.isPaid) {
        // Se estiver pago, criar como transa√ß√£o recorrente
        await apiRequest('POST', '/api/transactions', {
          description: data.name,
          amount: data.amount,
          type: 'expense',
          categoryId: data.categoryId,
          paymentMethod: 'other',
          date: new Date().toISOString().split('T')[0],
          isRecurring: true,
          dueDay: parseInt(data.dueDay),
          totalInstallments: data.totalInstallments ? parseInt(data.totalInstallments) : undefined,
          paidInstallments: data.paidInstallments ? parseInt(data.paidInstallments) : undefined,
        });
      } else {
        // Se n√£o estiver pago, criar como conta fixa
        await apiRequest('POST', '/api/fixed-expenses', {
          name: data.name,
          amount: data.amount,
          dueDay: parseInt(data.dueDay),
          categoryId: data.categoryId,
          totalInstallments: data.totalInstallments ? parseInt(data.totalInstallments) : undefined,
          paidInstallments: data.paidInstallments ? parseInt(data.paidInstallments) : undefined,
        });
      }
    },
    onSuccess: async () => {
      toast({
        title: "‚úÖ Sucesso",
        description: "Conta fixa criada com sucesso!",
      });
      form.reset();
      setIsDialogOpen(false);

      // Force clear cache and refetch
      queryClient.removeQueries({ queryKey: ['/api/transactions/recurring'] });
      queryClient.removeQueries({ queryKey: ['/api/fixed-expenses'] });
      queryClient.removeQueries({ queryKey: ['/api/transactions'] });
      queryClient.removeQueries({ queryKey: ['/api/transactions/future-commitments'] });
      queryClient.removeQueries({ queryKey: ['/api/financial-summary'] });

      // Force refetch immediately
      await queryClient.refetchQueries({ queryKey: ['/api/transactions/recurring'] });
    },
    onError: (error) => {
      if (isUnauthorizedError(error as Error)) {
        toast({
          title: "N√£o Autorizado",
          description: "Voc√™ foi desconectado. Fazendo login novamente...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Erro",
        description: "Falha ao criar conta fixa. Tente novamente.",
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: FixedExpenseFormData) => {
    createFixedExpenseMutation.mutate(data);
  };

  if (isLoading) {
    return (
      <Card className="financial-card">
        <CardHeader>
          <div className="flex items-center justify-between">
            <CardTitle>Contas Fixas do M√™s</CardTitle>
            <Skeleton className="h-9 w-32" />
          </div>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
            {Array.from({ length: 4 }).map((_, i) => (
              <Skeleton key={i} className="h-24 w-full rounded-lg" />
            ))}
          </div>
        </CardContent>
      </Card>
    );
  }

  const formatCurrency = (value: string) => {
    return new Intl.NumberFormat('pt-BR', {
      style: 'currency',
      currency: 'BRL',
    }).format(parseFloat(value));
  };

  const formatDueDate = (dueDay: number) => {
    const currentDate = new Date();
    const currentMonth = currentDate.getMonth();
    const currentYear = currentDate.getFullYear();
    const dueDate = new Date(currentYear, currentMonth, dueDay);

    return `Vence em ${dueDay.toString().padStart(2, '0')}/${(currentMonth + 1).toString().padStart(2, '0')}`;
  };

  return (
    <Card className="financial-card">
      <CardHeader>
        <div className="flex items-center justify-between">
          <CardTitle className="flex items-center gap-2">
            <Calendar className="w-5 h-5 text-blue-600" />
            Contas Fixas do M√™s
          </CardTitle>
          <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
            <DialogTrigger asChild>
              <Button 
                className="bg-blue-500 hover:bg-blue-600 text-white" 
                data-testid="button-add-fixed-expense"
              >
                <Plus className="w-4 h-4 mr-2" />
                Nova Conta Fixa
              </Button>
            </DialogTrigger>
