                            <Input
                              {...field}
                              type="number"
                              min="1"
                              max="31"
                              placeholder="Ex: 10"
                              data-testid="input-due-day"
                            />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                  </div>

                  {/* Categoria */}
                  <FormField
                    control={form.control}
                    name="categoryId"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Categoria</FormLabel>
                        <Select value={field.value} onValueChange={field.onChange}>
                          <FormControl>
                            <SelectTrigger data-testid="select-category">
                              <SelectValue placeholder="Selecione uma categoria" />
                            </SelectTrigger>
                          </FormControl>
                          <SelectContent>
                            {categories?.map((category: any) => (
                              <SelectItem key={category.id} value={category.id}>
                                {category.name}
                              </SelectItem>
                            ))}
                          </SelectContent>
                        </Select>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  {/* Controle de Parcelas */}
                  <div className="border rounded-lg p-4 space-y-4">
                    <h4 className="text-sm font-medium">üìä Controle de Parcelas (Opcional)</h4>

                    <div className="grid grid-cols-2 gap-4">
                      {/* Total de Parcelas */}
                      <FormField
                        control={form.control}
                        name="totalInstallments"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Total de Parcelas</FormLabel>
                            <FormControl>
                              <Input
                                {...field}
                                type="number"
                                min="1"
                                placeholder="Ex: 12"
                                data-testid="input-total-installments"
                              />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />

                      {/* Parcelas J√° Pagas */}
                      <FormField
                        control={form.control}
                        name="paidInstallments"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>J√° Pagas</FormLabel>
                            <FormControl>
                              <Input
                                {...field}
                                type="number"
                                min="0"
                                max={form.watch('totalInstallments') || "999"}
                                placeholder="Ex: 3"
                                data-testid="input-paid-installments"
                              />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                    </div>

                    {/* Mostrar Progress */}
                    {form.watch('totalInstallments') && form.watch('paidInstallments') && (
                      <div className="text-sm text-gray-600 dark:text-gray-400">
                        {(() => {
                          const total = parseInt(form.watch('totalInstallments') || '0');
                          const paid = parseInt(form.watch('paidInstallments') || '0');
                          const remaining = total - paid;
                          const percentage = total > 0 ? (paid / total) * 100 : 0;

                          return (
                            <div className="space-y-2">
                              <div className="flex justify-between text-xs">
                                <span>Progresso: {paid}/{total} parcelas</span>
                                <span>{remaining} restantes</span>
                              </div>
                              <div className="w-full bg-gray-200 rounded-full h-2">
                                <div 
                                  className="bg-blue-600 h-2 rounded-full" 
                                  style={{ width: `${percentage}%` }}
                                ></div>
                              </div>
                            </div>
                          );
                        })()}
                      </div>
                    )}
                  </div>

                  {/* Status Pago */}
                  <FormField
                    control={form.control}
                    name="isPaid"
                    render={({ field }) => (
                      <FormItem className="flex flex-row items-start space-x-3 space-y-0 rounded-md border p-4">
                        <FormControl>
                          <Checkbox
                            checked={field.value}
                            onCheckedChange={field.onChange}
                            data-testid="checkbox-paid"
                          />
                        </FormControl>
                        <div className="space-y-1 leading-none">
                          <FormLabel className="text-sm font-medium">
                            ‚úÖ J√° foi pago este m√™s?
                          </FormLabel>
                          <p className="text-xs text-gray-600 dark:text-gray-400">
                            Se j√° foi pago, aparecer√° nos lan√ßamentos. Se n√£o, ficar√° pendente.
                          </p>
                        </div>
                      </FormItem>
                    )}
                  />

                  <div className="flex justify-end space-x-4">
                    <Button 
                      type="button" 
                      variant="outline"
                      onClick={() => setIsDialogOpen(false)}
                      data-testid="button-cancel"
                    >
                      Cancelar
                    </Button>
                    <Button 
                      type="submit" 
                      disabled={createFixedExpenseMutation.isPending}
                      data-testid="button-save"
                    >
                      {createFixedExpenseMutation.isPending ? (
                        "Salvando..."
                      ) : (
                        <>
                          <Save className="w-4 h-4 mr-2" />
                          Salvar
                        </>
                      )}
                    </Button>
                  </div>
                </form>
              </Form>
            </DialogContent>
          </Dialog>
        </div>
      </CardHeader>
      <CardContent>
        {!expenses || expenses.length === 0 ? (
          <div className="text-center py-8 text-gray-500 dark:text-gray-400">
            <Calendar className="w-12 h-12 mx-auto mb-4 opacity-50" />
            <p>Nenhuma conta fixa cadastrada.</p>
            <p className="text-sm">Adicione suas contas mensais para melhor controle.</p>
          </div>
        ) : (
          <div className="space-y-4">
            {/* Navigation Header */}
            {expenses.length > 4 && (
              <div className="flex items-center justify-between">
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => setCurrentPage(Math.max(0, currentPage - 1))}
                  disabled={currentPage === 0}
                  className="flex items-center gap-1"
                  data-testid="previous-page"
                >
                  <ChevronLeft className="w-4 h-4" />
                </Button>

                <span className="text-sm text-gray-500 dark:text-gray-400">
                  {Math.min(currentPage * 4 + 1, expenses.length)} - {Math.min((currentPage + 1) * 4, expenses.length)} de {expenses.length}
                </span>

                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => setCurrentPage(Math.min(Math.ceil(expenses.length / 4) - 1, currentPage + 1))}
                  disabled={currentPage >= Math.ceil(expenses.length / 4) - 1}
                  className="flex items-center gap-1"
                  data-testid="next-page"
                >
                  <ChevronRight className="w-4 h-4" />
                </Button>
              </div>
            )}

            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
              {expenses.slice(currentPage * 4, (currentPage + 1) * 4).map((transaction: any) => {
                const Icon = getCategoryIcon(transaction.category?.name || '', transaction.description);

                return (
                  <div 
                    key={transaction.id}
                    className="border border-gray-200 dark:border-gray-600 rounded-lg p-4 hover:border-blue-300 dark:hover:border-blue-600 transition-colors"
                    data-testid={`fixed-expense-${transaction.id}`}
                  >
                    <div className="flex items-center justify-between mb-2">
                      <div className="flex items-center space-x-2">
                        <div className={`w-8 h-8 rounded-lg flex items-center justify-center ${getCategoryColor(transaction.description)}`}>
                          <Icon className="w-4 h-4" />
                        </div>
                        <span className="font-medium text-gray-900 dark:text-white text-sm" data-testid={`expense-name-${transaction.id}`}>
                          {transaction.description}
                        </span>
                      </div>
                      <span 
                        className={`w-2 h-2 bg-blue-500 rounded-full`} 
                        title="Lan√ßamento Mensal"
                        data-testid={`expense-status-${transaction.id}`}
                      ></span>
                    </div>
                    <p className="text-lg font-bold text-gray-900 dark:text-white" data-testid={`expense-amount-${transaction.id}`}>
                      {formatCurrency(transaction.amount)}
                    </p>
                    {transaction.totalInstallments && transaction.paidInstallments !== undefined && (
                      <p className="text-xs text-blue-600 dark:text-blue-400 font-medium mb-1">
                        üìä {transaction.paidInstallments}/{transaction.totalInstallments} parcelas
                      </p>
                    )}
                    <p className="text-xs text-gray-500 dark:text-gray-400" data-testid={`expense-due-date-${transaction.id}`}>
                      {transaction.dueDay ? formatDueDate(transaction.dueDay) : 'Data n√£o definida'}
                    </p>
                  </div>
                );
              })}
            </div>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

client/src/components/dashboard/recent-t.content:
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { useQuery } from "@tanstack/react-query";
import { Skeleton } from "@/components/ui/skeleton";
import { ShoppingCart, Briefcase, Car, Home, Utensils, HeartHandshake, Activity } from "lucide-react";
import { Link } from "wouter";
import { PAYMENT_METHODS, TRANSACTION_TYPES } from "@/lib/constants";

const getCategoryIcon = (categoryName: string) => {
  const name = categoryName.toLowerCase();
  if (name.includes('mercado') || name.includes('supermercado')) return ShoppingCart;
  if (name.includes('trabalho') || name.includes('sal√°rio')) return Briefcase;
  if (name.includes('transporte') || name.includes('combust√≠vel')) return Car;
  if (name.includes('casa') || name.includes('aluguel')) return Home;
  if (name.includes('alimenta√ß√£o') || name.includes('restaurante')) return Utensils;
  return HeartHandshake;
};

const getCategoryColor = (type: string) => {
  return type === 'income' ? 'text-green-600 dark:text-green-400 bg-green-100 dark:bg-green-900/30' : 'text-red-600 dark:text-red-400 bg-red-100 dark:bg-red-900/30';
};

// Fun√ß√£o para traduzir tipos de transa√ß√£o
const getTransactionTypeLabel = (type: string) => {
  const transactionType = TRANSACTION_TYPES.find(t => t.value === type);
  return transactionType?.label || type;
};

// Fun√ß√£o para traduzir m√©todos de pagamento
const getPaymentMethodLabel = (method: string) => {
  const paymentMethod = PAYMENT_METHODS.find(p => p.value === method);
  return paymentMethod?.label || method;
};

export function RecentTransactions() {
  const { data: transactionData, isLoading } = useQuery({
    queryKey: ['/api/transactions'],
    queryFn: async () => {
      const response = await fetch('/api/transactions?limit=5', {
        credentials: 'include',
      });
      if (!response.ok) throw new Error('Failed to fetch transactions');
      return response.json();
    },
  });

  const transactions = transactionData?.transactions || [];

  if (isLoading) {
    return (
      <Card className="financial-card">
        <CardHeader>
          <CardTitle>Transa√ß√µes Recentes</CardTitle>
        </CardHeader>
        <CardContent className="space-y-4">
          {Array.from({ length: 5 }).map((_, i) => (
            <div key={i} className="flex items-center space-x-4">
              <Skeleton className="h-10 w-10 rounded-lg" />
              <div className="flex-1 space-y-2">
                <Skeleton className="h-4 w-32" />
                <Skeleton className="h-3 w-20" />
              </div>
              <Skeleton className="h-6 w-20" />
            </div>
          ))}
        </CardContent>
      </Card>
    );
  }

  const formatCurrency = (value: string) => {
    return new Intl.NumberFormat('pt-BR', {
      style: 'currency',
      currency: 'BRL',
    }).format(parseFloat(value));
  };

  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    const now = new Date();
    const diffInDays = Math.floor((now.getTime() - date.getTime()) / (1000 * 60 * 60 * 24));

    if (diffInDays === 0) return 'Hoje';
    if (diffInDays === 1) return 'Ontem';
    return date.toLocaleDateString('pt-BR', { day: '2-digit', month: '2-digit' });
  };

  return (
    <Card className="modern-card shadow-lg">
      <CardHeader className="pb-4">
        <div className="flex items-center justify-between">
          <CardTitle className="flex items-center gap-3 text-xl font-bold text-gray-900 dark:text-white">
            <Activity className="w-5 h-5 text-green-600 dark:text-green-400" />
            Transa√ß√µes Recentes
          </CardTitle>
          <Button 
            variant="outline" 
            size="sm" 
            asChild 
            className="bg-emerald-50 hover:bg-emerald-100 text-emerald-700 border-emerald-200 dark:bg-emerald-900/20 dark:hover:bg-emerald-900/30 dark:text-emerald-400 dark:border-emerald-800 font-medium shadow-sm"
            data-testid="button-view-all-transactions"
          >
            <Link href="/transactions">Ver todas</Link>
          </Button>
        </div>
      </CardHeader>
      <CardContent>
        <div className="space-y-4">
          {transactions?.length === 0 ? (
            <div className="text-center py-8 text-gray-500 dark:text-gray-300">
              <p className="text-gray-600 dark:text-gray-300">Nenhuma transa√ß√£o encontrada.</p>
              <p className="text-sm text-gray-500 dark:text-gray-400">Adicione sua primeira transa√ß√£o para come√ßar.</p>
            </div>
          ) : (
            transactions?.map((transaction: any, index: number) => {
              const Icon = getCategoryIcon(transaction.description);

              return (
                <div 
                  key={transaction.id} 
                  className="flex items-center justify-between p-4 hover:bg-gray-50 dark:hover:bg-gray-700/50 rounded-xl transition-all duration-200 border border-transparent hover:border-gray-200 dark:hover:border-gray-600"
                  data-testid={`transaction-item-${index}`}
                >
                  <div className="flex items-center space-x-4">
                    <div className={`w-12 h-12 rounded-xl flex items-center justify-center ${getCategoryColor(transaction.type)}`}>
                      <Icon className="w-6 h-6" />
                    </div>
                    <div className="flex-1">
                      <p className="font-semibold text-gray-900 dark:text-white leading-tight" data-testid={`transaction-description-${index}`}>
                        {transaction.description}
                      </p>
                      <p className="text-sm text-gray-500 dark:text-gray-400 font-medium mt-1" data-testid={`transaction-date-${index}`}>
                        {formatDate(transaction.date)} ‚Ä¢ {getPaymentMethodLabel(transaction.paymentMethod)}
                      </p>
                    </div>
                  </div>
                  <div className="text-right">
                    <p className={`text-lg font-bold tracking-tight ${transaction.type === 'income' ? 'text-green-600 dark:text-green-400' : 'text-red-600 dark:text-red-400'}`} data-testid={`transaction-amount-${index}`}>
                      {transaction.type === 'income' ? '+' : '-'}{formatCurrency(transaction.amount)}
                    </p>
                    {transaction.totalInstallments && transaction.paidInstallments !== undefined && (
                      <p className="text-xs text-blue-600 dark:text-blue-400 font-semibold mt-1">
                        {transaction.paidInstallments}/{transaction.totalInstallments} parcelas
                      </p>
                    )}
                  </div>
                </div>
              );
            })
          )}
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/investments/invest.content:
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { useQuery } from "@tanstack/react-query";
import { Skeleton } from "@/components/ui/skeleton";
import { TrendingUp, Target, Trophy, Star, Zap, Crown, Medal, Award, Flame, Gift } from "lucide-react";
import { InvestmentGoals } from "./investment-goals";
import { Progress } from "@/components/ui/progress";
import { Badge } from "@/components/ui/badge";

export function InvestmentOverview() {

  const { data: investments, isLoading } = useQuery({
    queryKey: ['/api/investments'],
    queryFn: async () => {
      const response = await fetch('/api/investments', {
        credentials: 'include',
      });
      if (!response.ok) throw new Error('Failed to fetch investments');
      return response.json();
    },
  });

  // Sistema de gamifica√ß√£o
  const getInvestorLevel = (totalValue: number, investmentCount: number) => {
    if (totalValue === 0) {
      return { name: 'Iniciante', icon: Star, color: 'gray', progress: 0, nextLevel: 'Novato', requirement: 'Fa√ßa seu primeiro investimento' };
    }
    if (totalValue < 1000 || investmentCount < 3) {
      return { name: 'Novato', icon: Zap, color: 'blue', progress: Math.min((totalValue / 1000) * 100, 100), nextLevel: 'Explorador', requirement: 'R$ 1.000 investidos + 3 ativos' };
    }
    if (totalValue < 5000 || investmentCount < 5) {
      return { name: 'Explorador', icon: Target, color: 'green', progress: Math.min(((totalValue - 1000) / 4000) * 100, 100), nextLevel: 'Estrategista', requirement: 'R$ 5.000 investidos + 5 ativos' };
    }
    if (totalValue < 15000 || investmentCount < 8) {
      return { name: 'Estrategista', icon: Trophy, color: 'purple', progress: Math.min(((totalValue - 5000) / 10000) * 100, 100), nextLevel: 'Mestre', requirement: 'R$ 15.000 investidos + 8 ativos' };
    }
    return { name: 'Mestre', icon: Crown, color: 'yellow', progress: 100, nextLevel: null, requirement: 'N√≠vel m√°ximo atingido!' };
  };

  const getAchievements = (totalValue: number, investmentCount: number, totalGain: number) => {
    const achievements = [
      {
        id: 'first-investment',
        title: 'Primeiro Passo',
        description: 'Realizou o primeiro investimento',
        icon: Medal,
        unlocked: investmentCount > 0,
        color: 'bg-blue-500'
      },
      {
        id: 'diversified',
        title: 'Diversificado',
        description: 'Possui 3 ou mais investimentos',
        icon: Award,
        unlocked: investmentCount >= 3,
        color: 'bg-green-500'
      },
      {
        id: 'thousand-club',
        title: 'Clube dos Mil',
        description: 'Investiu mais de R$ 1.000',
        icon: Star,
        unlocked: totalValue >= 1000,
        color: 'bg-purple-500'
      },
      {
        id: 'profitable',
        title: 'Lucrativo',
        description: 'Teve ganhos positivos',
        icon: TrendingUp,
        unlocked: totalGain > 0,
        color: 'bg-emerald-500'
      },
      {
        id: 'five-thousand',
        title: 'Investidor S√©rio',
        description: 'Patrim√¥nio de R$ 5.000+',
        icon: Trophy,
        unlocked: totalValue >= 5000,
        color: 'bg-orange-500'
      },
      {
        id: 'master-investor',
        title: 'Mestre Investidor',
        description: 'Patrim√¥nio de R$ 15.000+',
        icon: Crown,
        unlocked: totalValue >= 15000,
        color: 'bg-yellow-500'
      }
    ];
    return achievements;
  };

  const formatCurrency = (value: number) => {
    return new Intl.NumberFormat('pt-BR', {
      style: 'currency',
      currency: 'BRL',
    }).format(value);
  };

  const calculateTotalValue = () => {
    if (!investments) return 0;
    return investments.reduce((total: number, investment: any) => {
      return total + parseFloat(investment.currentAmount);
    }, 0);
  };

  const calculateTotalGain = () => {
    if (!investments) return { amount: 0, percentage: 0 };

    const totalCurrent = calculateTotalValue();
    const totalInitial = investments.reduce((total: number, investment: any) => {
      return total + parseFloat(investment.initialAmount);
    }, 0);

    const gain = totalCurrent - totalInitial;
    const percentage = totalInitial > 0 ? (gain / totalInitial) * 100 : 0;

    return { amount: gain, percentage };
  };

  const totalValue = calculateTotalValue();
  const gainData = calculateTotalGain();
  const totalGain = gainData.amount;
  const gainPercentage = gainData.percentage;

  if (isLoading) {
    return (
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        <div className="lg:col-span-2">
          <Card>
            <CardHeader>
              <CardTitle>üéÆ Evolu√ß√£o Gamificada</CardTitle>
            </CardHeader>
            <CardContent>
              <Skeleton className="h-80 w-full" />
            </CardContent>
          </Card>
        </div>

        <div className="space-y-6">
          <Skeleton className="h-40 w-full rounded-xl" />
          <Skeleton className="h-32 w-full rounded-xl" />
        </div>
      </div>
    );
  }

  const investmentCount = investments?.length || 0;
  const level = getInvestorLevel(totalValue, investmentCount);
  const achievements = getAchievements(totalValue, investmentCount, totalGain);
  const unlockedAchievements = achievements.filter((a: any) => a.unlocked);
  const nextAchievement = achievements.find((a: any) => !a.unlocked);

  return (
    <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
      <div className="lg:col-span-2">
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              üéÆ Evolu√ß√£o Gamificada
              <Badge variant="outline" className="ml-auto">
                {unlockedAchievements.length}/{achievements.length} conquistas
              </Badge>
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-6">
            {/* N√≠vel do Investidor */}
            <div className="bg-gradient-to-r from-blue-50 to-purple-50 dark:from-blue-950 dark:to-purple-950 p-6 rounded-xl border">
              <div className="flex items-center justify-between mb-4">
                <div className="flex items-center gap-3">
                  <div className={`w-12 h-12 bg-${level.color}-100 dark:bg-${level.color}-900 rounded-full flex items-center justify-center`}>
                    <level.icon className={`w-6 h-6 text-${level.color}-600`} />
                  </div>
                  <div>
                    <h3 className="text-xl font-bold text-gray-900 dark:text-white">{level.name}</h3>
                    <p className="text-sm text-gray-600 dark:text-gray-400">
                      {level.nextLevel ? `Pr√≥ximo: ${level.nextLevel}` : 'N√≠vel m√°ximo!'}
                    </p>
                  </div>
                </div>
                <div className="text-right">
                  <p className="text-2xl font-bold text-gray-900 dark:text-white">
                    {formatCurrency(totalValue)}
                  </p>
                  <p className="text-sm text-gray-600 dark:text-gray-400">
                    {investmentCount} {investmentCount === 1 ? 'ativo' : 'ativos'}
                  </p>
                </div>
              </div>

              {level.nextLevel && (
                <div className="space-y-2">
                  <div className="flex justify-between text-sm">
                    <span className="text-gray-600 dark:text-gray-400">Progresso para {level.nextLevel}</span>
                    <span className="font-medium">{Math.round(level.progress)}%</span>
                  </div>
                  <Progress value={level.progress} className="h-3" />
                  <p className="text-xs text-gray-500 dark:text-gray-400">
                    üìã {level.requirement}
                  </p>
                </div>
              )}
            </div>

            {/* Conquistas */}
            <div>
              <h4 className="text-lg font-semibold mb-4 flex items-center gap-2">
                üèÜ Conquistas
                {nextAchievement && (
                  <Badge variant="secondary" className="text-xs">
                    Pr√≥xima: {nextAchievement.title}
                  </Badge>
                )}
              </h4>

              <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
                {achievements.map((achievement) => (
                  <div 
                    key={achievement.id}
                    className={`relative p-4 rounded-lg border transition-all duration-300 ${
                      achievement.unlocked 
                        ? 'bg-white dark:bg-gray-800 border-green-200 dark:border-green-800 shadow-lg transform scale-105' 
                        : 'bg-gray-50 dark:bg-gray-900 border-gray-200 dark:border-gray-700 opacity-60'
                    }`}
                  >
                    {achievement.unlocked && (
                      <div className="absolute -top-2 -right-2">
                        <div className="w-6 h-6 bg-green-500 rounded-full flex items-center justify-center">
                          <Star className="w-3 h-3 text-white" />
                        </div>
                      </div>
                    )}

                    <div className={`w-10 h-10 ${achievement.unlocked ? achievement.color : 'bg-gray-300'} rounded-lg flex items-center justify-center mb-3`}>
                      <achievement.icon className="w-5 h-5 text-white" />
                    </div>

                    <h5 className="font-medium text-sm mb-1">{achievement.title}</h5>
                    <p className="text-xs text-gray-600 dark:text-gray-400">
                      {achievement.description}
                    </p>
                  </div>
                ))}
              </div>
            </div>

            {(!investments || investments.length === 0) && (
              <div className="bg-gradient-to-r from-green-50 to-blue-50 dark:from-green-950 dark:to-blue-950 p-6 rounded-xl border border-green-200 dark:border-green-800">
                <div className="flex items-center gap-3 mb-3">
                  <div className="w-10 h-10 bg-green-100 dark:bg-green-900 rounded-lg flex items-center justify-center">
                    <Gift className="w-5 h-5 text-green-600" />
                  </div>
                  <div>
                    <h4 className="font-semibold text-green-900 dark:text-green-100">üöÄ Comece sua jornada!</h4>
                    <p className="text-sm text-green-700 dark:text-green-300">Fa√ßa seu primeiro investimento e desbloqueie conquistas</p>
                  </div>
                </div>
                <p className="text-sm text-green-600 dark:text-green-400">
                  üí° V√° para a se√ß√£o "Portf√≥lio" abaixo e adicione seu primeiro investimento para come√ßar a ganhar XP e medalhas!
                </p>
              </div>
            )}
          </CardContent>
        </Card>
      </div>

      <div className="space-y-6">
        {/* Investment Summary */}
        <Card className="bg-gradient-to-br from-purple-500 to-purple-600 text-white border-0">
          <CardContent className="p-6">
            <div className="flex items-center justify-between mb-4">
              <div>
                <p className="text-purple-100 text-sm font-medium">Total Investido</p>
                <p className="text-2xl font-bold" data-testid="total-investment-value">
                  {formatCurrency(totalValue)}
                </p>
              </div>
              <div className="bg-purple-400 bg-opacity-30 rounded-lg p-3">
                <TrendingUp className="w-6 h-6" />
              </div>
            </div>
            <div className="flex items-center text-purple-100 text-sm">
              <TrendingUp className="w-4 h-4 mr-1" />
              <span data-testid="investment-gain">
                {totalGain >= 0 ? '+' : ''}{formatCurrency(totalGain)} ({gainPercentage.toFixed(1)}%) este ano
              </span>
            </div>
          </CardContent>
        </Card>

        {/* Monthly Investment Goal */}
        <InvestmentGoals />
      </div>
    </div>
  );
}

