import { useState } from "react";
import { Link, useLocation } from "wouter";
import { cn } from "@/lib/utils";
import { useTheme } from "@/components/ui/theme-provider";
import { useBusinessTheme } from "@/hooks/useBusinessTheme";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { TrendingUp, Home, ArrowLeftRight, PieChart, FileText, Tags, User, Moon, Sun, Menu, X, Target, Upload, Crown, Building2, Users, Receipt, Package, Briefcase, LogOut, Brain, Zap, DollarSign, Calendar } from "lucide-react";

const getNavigation = (isBusinessAccount: boolean) => {
  const baseNavigation = [
    { name: isBusinessAccount ? 'Painel' : 'Dashboard', href: '/', icon: Home },
    { name: isBusinessAccount ? 'Movimenta√ß√µes' : 'Lan√ßamentos', href: '/transactions', icon: ArrowLeftRight },
    { name: 'Investimentos', href: '/investments', icon: PieChart },
    { name: isBusinessAccount ? 'Or√ßamentos' : 'Metas', href: '/goals', icon: Target },
    { name: 'Importa√ß√£o', href: '/import', icon: Upload },
    { name: 'Relat√≥rios', href: '/reports', icon: FileText },
    { name: 'IA Avan√ßada', href: '/advanced', icon: Brain },
    { name: 'Categorias', href: '/categories', icon: Tags },
  ];

  // Funcionalidades Nexo
  const nexoFeatures = [
    { name: 'Patrim√¥nio 360¬∫', href: '/assets', icon: Building2 },
    { name: 'Assinaturas', href: '/subscriptions', icon: Calendar },
    { name: 'Metas Nexo', href: '/nexo-goals', icon: Zap },
    { name: 'C√©rebro Anal√≠tico', href: '/analytics', icon: Brain },
  ];

  if (isBusinessAccount) {
    return [
      ...baseNavigation,
      ...nexoFeatures,
      { name: 'Fornecedores', href: '/suppliers', icon: Building2 },
      { name: 'Departamentos', href: '/departments', icon: Users },
      { name: 'Notas Fiscais', href: '/invoices', icon: Receipt },
      { name: 'Premium', href: '/subscription', icon: Crown },
    ];
  }

  return [
    ...baseNavigation,
    ...nexoFeatures,
    { name: 'Premium', href: '/subscription', icon: Crown },
  ];
};

export function Sidebar() {
  const [location] = useLocation();
  const { theme, setTheme } = useTheme();
  const { isBusinessAccount } = useBusinessTheme();
  const [isOpen, setIsOpen] = useState(false);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const navigation = getNavigation(isBusinessAccount);

  // Mutation to toggle business mode
  const toggleBusinessModeMutation = useMutation({
    mutationFn: async () => {
      const newAccountType = isBusinessAccount ? 'individual' : 'business';
      const response = await apiRequest("PATCH", "/api/user/profile", {
        accountType: newAccountType
      });
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: isBusinessAccount ? "Modo Individual ativado" : "Modo Empresarial ativado",
        description: isBusinessAccount 
          ? "Voc√™ est√° agora no sistema pessoal/familiar." 
          : "Voc√™ est√° agora no sistema empresarial.",
      });
      // Invalidate user data to refresh the interface
      queryClient.invalidateQueries({ queryKey: ["/api/auth/user"] });
    },
    onError: (error: any) => {
      toast({
        title: "Erro",
        description: error.message || "Erro ao alterar modo do sistema.",
        variant: "destructive",
      });
    },
  });


  return (
    <>
      {/* Mobile menu button */}
      <button
        onClick={() => setIsOpen(!isOpen)}
        className="fixed top-4 left-4 z-50 md:hidden p-3 rounded-lg bg-white dark:bg-gray-800 shadow-lg border border-gray-200 dark:border-gray-600 hover:bg-gray-50 dark:hover:bg-gray-700 transition-colors"
        data-testid="button-mobile-menu"
        aria-label={isOpen ? "Fechar menu" : "Abrir menu"}
      >
        {isOpen ? <X className="w-6 h-6 text-gray-600 dark:text-gray-300" /> : <Menu className="w-6 h-6 text-gray-600 dark:text-gray-300" />}
      </button>

      {/* Menu indicator for desktop - always visible */}
      <div className="hidden md:block fixed top-4 left-4 z-30 pointer-events-none">
        <div className="flex items-center space-x-2 text-xs text-gray-500 dark:text-gray-400">
          <Menu className="w-4 h-4" />
          <span>Menu</span>
        </div>
      </div>

      {/* Sidebar overlay for mobile */}
      {isOpen && (
        <div 
          className="fixed inset-0 bg-black bg-opacity-50 z-40 md:hidden"
          onClick={() => setIsOpen(false)}
        />
      )}

      {/* Sidebar */}
      <aside className={cn(
        "fixed md:static inset-y-0 left-0 z-40 w-64 md:w-64 h-screen md:h-full flex flex-col transition-transform duration-300 ease-in-out shadow-lg md:shadow-none",
        "md:translate-x-0 md:flex-shrink-0",
        isOpen ? "translate-x-0" : "-translate-x-full",
        isBusinessAccount 
          ? "bg-gradient-to-b from-white to-slate-50 border-r border-slate-200" 
          : "bg-white dark:bg-gray-800 border-r border-gray-200 dark:border-gray-700"
      )}>
        {/* Logo */}
        <div className="p-6">
          <div className="flex items-center space-x-3">
            <div className={cn(
              "w-10 h-10 rounded-lg flex items-center justify-center",
              isBusinessAccount 
                ? "bg-gradient-to-r from-slate-700 to-slate-800" 
                : "bg-gradient-to-r from-blue-500 to-blue-600"
            )}>
              <TrendingUp className="text-white text-lg" />
            </div>
            <div>
              <h1 className={cn(
                "text-xl font-bold",
                isBusinessAccount ? "text-slate-800" : "text-gray-900 dark:text-white"
              )}>
                FinanceFlow
              </h1>
              {isBusinessAccount && (
                <span className="text-xs text-slate-600 font-medium">EMPRESARIAL</span>
              )}
            </div>
          </div>
        </div>

        {/* Navigation */}
        <nav className="flex-1 mt-8 overflow-y-auto">
          <div className="px-6 space-y-2 pb-6">
            {navigation.map((item) => {
              const Icon = item.icon;
              const isActive = location === item.href;

              return (
                <Link
                  key={item.name}
                  href={item.href}
                  className={cn(
                    "sidebar-item",
                    isActive && "active",
                    item.name === "Premium" && "premium-nav-item"
                  )}
                  data-testid={`nav-${item.name.toLowerCase()}`}
                  onClick={() => setIsOpen(false)}
                >
                  <Icon className={cn(
                    "w-5 h-5 sidebar-icon", 
                    isActive ? "sidebar-icon-active" : "",
                    item.name === "Premium" ? "text-yellow-500" : isActive ? "text-white" : "text-gray-600 dark:text-gray-300"
                  )} />
                  <span className={item.name === "Premium" ? "font-semibold text-yellow-600 dark:text-yellow-500" : ""}>{item.name}</span>
                </Link>
              );
            })}
          </div>

          {/* Settings Section */}
          <div className="px-6 mt-8 pt-8 border-t border-gray-200 dark:border-gray-700">
            <div className="space-y-2 pb-6">
              <Link
                href="/profile"
                className={cn(
                  "sidebar-item",
                  location === "/profile" && "active"
                )}
                data-testid="nav-profile"
                onClick={() => setIsOpen(false)}
              >
                <User className={cn("w-5 h-5 sidebar-icon", location === "/profile" ? "sidebar-icon-active text-white" : "text-gray-600 dark:text-gray-300")} />
                <span>Perfil</span>
              </Link>

              <button
                onClick={() => setTheme(theme === "dark" ? "light" : "dark")}
                className="sidebar-item w-full text-left"
                data-testid="button-theme-toggle"
              >
                {theme === "dark" ? <Sun className="w-5 h-5 sidebar-icon text-gray-600 dark:text-gray-300" /> : <Moon className="w-5 h-5 sidebar-icon text-gray-600 dark:text-gray-300" />}
                <span>{theme === "dark" ? "Modo Claro" : "Modo Escuro"}</span>
              </button>

              <button
                onClick={() => toggleBusinessModeMutation.mutate()}
                className="sidebar-item w-full text-left"
                data-testid="button-business-mode-toggle"
                disabled={toggleBusinessModeMutation.isPending}
              >
                <Briefcase className={cn(
                  "w-5 h-5 sidebar-icon",
                  isBusinessAccount ? "text-blue-600 dark:text-blue-400" : "text-gray-600 dark:text-gray-300"
                )} />
                <span className={isBusinessAccount ? "text-blue-600 dark:text-blue-400 font-medium" : ""}>
                  {toggleBusinessModeMutation.isPending ? 'Alterando...' : 
                   isBusinessAccount ? "Modo Individual" : "Modo Empresarial"}
                </span>
              </button>

              <div className="my-2"></div>

              <button
                onClick={() => window.location.href = '/api/logout'}
                className="sidebar-item w-full text-left hover:bg-red-50 dark:hover:bg-red-900/20 hover:text-red-600 dark:hover:text-red-400"
                data-testid="button-logout"
              >
                <LogOut className="w-5 h-5 sidebar-icon text-gray-600 dark:text-gray-300" />
                <span>Sair</span>
              </button>
            </div>
          </div>
        </nav>

      </aside>
    </>
  );
}

client/src/components/transactions/trans.content:
import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group";
import { Label } from "@/components/ui/label";
import { Checkbox } from "@/components/ui/checkbox";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { isUnauthorizedError } from "@/lib/authUtils";
import { PAYMENT_METHODS } from "@/lib/constants";
import { Save, Upload } from "lucide-react";

const transactionSchema = z.object({
  description: z.string().min(1, "Descri√ß√£o √© obrigat√≥ria"),
  amount: z.string().min(1, "Valor √© obrigat√≥rio").refine((val) => !isNaN(Number(val)) && Number(val) > 0, "Valor deve ser um n√∫mero positivo"),
  type: z.enum(["income", "expense"]),
  categoryId: z.string().optional(),
  paymentMethod: z.enum(["pix", "debit_card", "credit_card", "cash", "transfer", "other"]),
  date: z.string().min(1, "Data √© obrigat√≥ria"),
  isRecurring: z.boolean().default(false),
  dueDay: z.string().optional(),
  // Campos para parcelamento
  isInstallment: z.boolean().default(false),
  totalValue: z.string().optional(),
  installmentCount: z.string().optional(),
  installmentValue: z.string().optional(),
});

type TransactionFormData = z.infer<typeof transactionSchema>;

interface TransactionFormProps {
  editingTransaction?: any;
  onSuccess?: () => void;
}

export function TransactionForm({ editingTransaction, onSuccess }: TransactionFormProps = {}) {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const isEditing = !!editingTransaction;

  const form = useForm<TransactionFormData>({
    resolver: zodResolver(transactionSchema),
    defaultValues: editingTransaction ? {
      description: editingTransaction.description || "",
      amount: editingTransaction.amount?.toString() || "",
      type: editingTransaction.type || "expense",
      categoryId: editingTransaction.categoryId || "",
      paymentMethod: editingTransaction.paymentMethod || "pix",
      date: editingTransaction.date ? editingTransaction.date.split('T')[0] : new Date().toISOString().split('T')[0],
      isRecurring: editingTransaction.isRecurring || false,
      dueDay: editingTransaction.dueDay?.toString() || "",
      isInstallment: editingTransaction.isInstallment || false,
      totalValue: editingTransaction.totalValue?.toString() || "",
      installmentCount: editingTransaction.totalInstallments?.toString() || "",
      installmentValue: editingTransaction.amount?.toString() || "",
    } : {
      type: "expense",
      date: new Date().toISOString().split('T')[0],
      paymentMethod: "pix",
      isRecurring: false,
      isInstallment: false,
    },
  });

  const watchPaymentMethod = form.watch("paymentMethod");
  const watchIsInstallment = form.watch("isInstallment");
  const watchInstallmentCount = form.watch("installmentCount");
  const watchTotalValue = form.watch("totalValue");

  // Auto-calculate installment value when total value or installment count changes
  const calculateInstallmentValue = () => {
    if (watchTotalValue && watchInstallmentCount) {
      const total = parseFloat(watchTotalValue);
      const count = parseInt(watchInstallmentCount);
      if (!isNaN(total) && !isNaN(count) && count > 0) {
        const installmentValue = (total / count).toFixed(2);
        form.setValue("installmentValue", installmentValue);
        form.setValue("amount", installmentValue); // Amount becomes the installment value
      }
    }
  };

  const { data: categories } = useQuery({
    queryKey: ['/api/categories'],
    queryFn: async () => {
      const response = await fetch('/api/categories', {
        credentials: 'include',
      });
      if (!response.ok) throw new Error('Failed to fetch categories');
      return response.json();
    },
  });

  const saveTransactionMutation = useMutation({
    mutationFn: async (data: TransactionFormData) => {
      const endpoint = isEditing ? `/api/transactions/${editingTransaction.id}` : '/api/transactions';
      const method = isEditing ? 'PUT' : 'POST';

      await apiRequest(method, endpoint, {
        ...data,
        amount: data.amount,
        date: data.date,
        isRecurring: data.isRecurring,
        dueDay: data.isRecurring && data.dueDay ? parseInt(data.dueDay) : undefined,
        // Installment data
        totalValue: data.isInstallment && data.totalValue ? parseFloat(data.totalValue) : undefined,
        totalInstallments: data.isInstallment && data.installmentCount ? parseInt(data.installmentCount) : undefined,
        paidInstallments: data.isInstallment ? (editingTransaction?.paidInstallments || 1) : undefined,
      });
    },
    onSuccess: () => {
      toast({
        title: "Sucesso",
        description: isEditing ? "Transa√ß√£o atualizada com sucesso!" : "Transa√ß√£o criada com sucesso!",
      });
      if (!isEditing) {
        form.reset();
        setSelectedFile(null);
      }
      queryClient.invalidateQueries({ queryKey: ['/api/transactions'] });
      queryClient.invalidateQueries({ queryKey: ['/api/transactions/future-commitments'] });
      queryClient.invalidateQueries({ queryKey: ['/api/financial-summary'] });
      onSuccess?.();
    },
    onError: (error) => {
      if (isUnauthorizedError(error as Error)) {
        toast({
          title: "N√£o Autorizado",
          description: "Voc√™ foi desconectado. Fazendo login novamente...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Erro",
        description: isEditing ? "Falha ao atualizar transa√ß√£o. Tente novamente." : "Falha ao criar transa√ß√£o. Tente novamente.",
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: TransactionFormData) => {
    saveTransactionMutation.mutate(data);
  };

  const handleFileChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    setSelectedFile(file || null);
  };

  return (
    <Card className={isEditing ? "" : "financial-card"}>
      {!isEditing && (
        <CardHeader>
          <CardTitle>Nova Transa√ß√£o</CardTitle>
        </CardHeader>
      )}
      <CardContent>
        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              {/* Transaction Type */}
              <FormField
                control={form.control}
                name="type"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Tipo</FormLabel>
                    <FormControl>
                      <RadioGroup
                        value={field.value}
                        onValueChange={field.onChange}
                        className="flex space-x-4"
                      >
                        <div className="flex items-center space-x-2">
                          <RadioGroupItem value="income" id="income" data-testid="radio-income" />
                          <Label htmlFor="income">Receita</Label>
                        </div>
                        <div className="flex items-center space-x-2">
                          <RadioGroupItem value="expense" id="expense" data-testid="radio-expense" />
                          <Label htmlFor="expense">Despesa</Label>
                        </div>
                      </RadioGroup>
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              {/* Amount */}
              <FormField
                control={form.control}
                name="amount"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Valor</FormLabel>
                    <FormControl>
                      <div className="relative">
                        <span className="absolute left-3 top-3 text-gray-500 dark:text-gray-400">R$</span>
                        <Input
                          {...field}
                          type="number"
                          step="0.01"
                          placeholder="0,00"
                          className="pl-8"
                          data-testid="input-amount"
                        />
                      </div>
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              {/* Description */}
              <FormField
                control={form.control}
                name="description"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Descri√ß√£o</FormLabel>
                    <FormControl>
                      <Input 
                        {...field} 
                        placeholder="Ex: Supermercado, Sal√°rio..."
                        data-testid="input-description"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              {/* Category */}
              <FormField
                control={form.control}
                name="categoryId"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Categoria</FormLabel>
                    <Select value={field.value} onValueChange={field.onChange}>
                      <FormControl>
                        <SelectTrigger data-testid="select-category">
                          <SelectValue placeholder="Selecione uma categoria" />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        {categories?.map((category) => (
                          <SelectItem key={category.id} value={category.id}>
                            {category.name}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              {/* Date */}
              <FormField
                control={form.control}
                name="date"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Data</FormLabel>
                    <FormControl>
                      <Input 
                        {...field} 
                        type="date"
                        data-testid="input-date"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              {/* Payment Method */}
              <FormField
                control={form.control}
                name="paymentMethod"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Forma de Pagamento</FormLabel>
                    <Select value={field.value} onValueChange={field.onChange}>
                      <FormControl>
                        <SelectTrigger data-testid="select-payment-method">
                          <SelectValue />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        {PAYMENT_METHODS.map((method) => (
                          <SelectItem key={method.value} value={method.value}>
                            {method.label}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />

              {/* Due Day - only show if recurring */}
              {form.watch('isRecurring') && (
                <FormField
                  control={form.control}
                  name="dueDay"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Dia do Vencimento</FormLabel>
                      <FormControl>
                        <Input
                          {...field}
                          type="number"
                          min="1"
                          max="31"
                          placeholder="Ex: 10"
                          data-testid="input-due-day"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
              )}
            </div>

            {/* Installment Checkbox - show for credit card or other (financing) */}
            {(watchPaymentMethod === 'credit_card' || watchPaymentMethod === 'other') && (
              <FormField
                control={form.control}
                name="isInstallment"
                render={({ field }) => (
                  <FormItem className="flex flex-row items-start space-x-3 space-y-0 rounded-md border p-4">
                    <FormControl>
                      <Checkbox
                        checked={field.value}
                        onCheckedChange={field.onChange}
                        data-testid="checkbox-installment"
                      />
                    </FormControl>
                    <div className="space-y-1 leading-none">
                      <FormLabel className="text-sm font-medium">
                        üè™ √â um produto parcelado?
                      </FormLabel>
                      <p className="text-xs text-gray-600 dark:text-gray-400">
                        Marque se este produto foi comprado a prazo ou financiado.
                      </p>
                    </div>
                  </FormItem>
                )}
              />
            )}

            {/* Installment Fields - show when installment is checked */}
            {watchIsInstallment && (watchPaymentMethod === 'credit_card' || watchPaymentMethod === 'other') && (
              <div className="border rounded-lg p-4 space-y-4">
                <h4 className="text-sm font-medium">üõí Dados do Parcelamento</h4>

                <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                  {/* Total Value */}
                  <FormField
                    control={form.control}
                    name="totalValue"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Valor Total do Bem</FormLabel>
                        <FormControl>
                          <div className="relative">
                            <span className="absolute left-3 top-3 text-gray-500 dark:text-gray-400">R$</span>
                            <Input
                              {...field}
                              type="number"
                              step="0.01"
                              placeholder="1200,00"
                              className="pl-8"
                              onChange={(e) => {
                                field.onChange(e);
                                setTimeout(calculateInstallmentValue, 100);
                              }}
                              data-testid="input-total-value"
                            />
                          </div>
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  {/* Installment Count */}
                  <FormField
                    control={form.control}
                    name="installmentCount"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>N√∫mero de Parcelas</FormLabel>
                        <FormControl>
                          <Input
                            {...field}
                            type="number"
                            min="2"
                            max="60"
                            placeholder="12"
                            onChange={(e) => {
                              field.onChange(e);
                              setTimeout(calculateInstallmentValue, 100);
                            }}
                            data-testid="input-installment-count"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  {/* Installment Value (auto-calculated) */}
                  <FormField
                    control={form.control}
                    name="installmentValue"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Valor da Parcela</FormLabel>
                        <FormControl>
                          <div className="relative">
                            <span className="absolute left-3 top-3 text-gray-500 dark:text-gray-400">R$</span>
                            <Input
                              {...field}
                              type="number"
                              step="0.01"
                              placeholder="100,00"
                              className="pl-8 bg-gray-50 dark:bg-gray-800"
                              readOnly
                              data-testid="input-installment-value"
                            />
                          </div>
                        </FormControl>
                        <p className="text-xs text-blue-600 dark:text-blue-400 mt-1">
                          Calculado automaticamente
                        </p>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                </div>
              </div>
            )}

            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              {/* Attachment */}
              <div>
                <Label>Comprovante</Label>
                <div className="mt-1 flex justify-center px-6 pt-5 pb-6 border-2 border-gray-300 dark:border-gray-600 border-dashed rounded-lg hover:border-blue-400 transition-colors">
                  <div className="space-y-1 text-center">
                    <Upload className="mx-auto h-8 w-8 text-gray-400" />
                    <div className="text-sm text-gray-600 dark:text-gray-400">
                      <label className="cursor-pointer text-blue-600 hover:text-blue-700">
                        <span>Carregar arquivo</span>
                        <input
                          type="file"
                          className="sr-only"
                          accept=".pdf,.jpg,.jpeg,.png"
                          onChange={handleFileChange}
                          data-testid="input-attachment"
                        />
                      </label>
                    </div>
                    {selectedFile && (
                      <p className="text-xs text-gray-500">{selectedFile.name}</p>
                    )}
                  </div>
                </div>
              </div>

              {/* Empty space for second column on larger screens */}
              <div className="hidden md:block"></div>
            </div>

            {/* Recurring Transaction Option */}
            <div className="border-t pt-6">
              <FormField
                control={form.control}
                name="isRecurring"
                render={({ field }) => (
                  <FormItem className="flex flex-row items-start space-x-3 space-y-0">
                    <FormControl>
                      <Checkbox
                        checked={field.value}
                        onCheckedChange={field.onChange}
                        data-testid="checkbox-recurring"
                      />
                    </FormControl>
                    <div className="space-y-1 leading-none">
                      <FormLabel className="text-sm font-medium">
                        üìÖ √â um lan√ßamento mensal?
                      </FormLabel>
                      <p className="text-xs text-gray-600 dark:text-gray-400">
                        Marque se esta √© uma despesa ou receita que se repete mensalmente (ex: aluguel, sal√°rio, conta de luz)
                      </p>
                    </div>
                  </FormItem>
                )}
              />
            </div>

            <div className="flex justify-end space-x-4">
              <Button 
                type="button" 
                variant="outline"
                onClick={() => form.reset()}
                data-testid="button-cancel"
              >
                Cancelar
              </Button>
              <Button 
                type="submit" 
                disabled={saveTransactionMutation.isPending}
                className="bg-blue-500 hover:bg-blue-600 text-white"
                data-testid="button-save-transaction"
              >
                {saveTransactionMutation.isPending ? (
                  isEditing ? "Atualizando..." : "Salvando..."
                ) : (
                  <>
                    <Save className="w-4 h-4 mr-2" />
                    {isEditing ? "Atualizar Transa√ß√£o" : "Salvar Transa√ß√£o"}
                  </>
                )}
              </Button>
            </div>
          </form>
        </Form>
      </CardContent>
    </Card>
  );
}

import React, { useState, useMemo } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Skeleton } from "@/components/ui/skeleton";
import { Checkbox } from "@/components/ui/checkbox";
import { Edit, Trash2, Download, Filter, X, Check, Search, FilterX } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { isUnauthorizedError } from "@/lib/authUtils";
import { PAYMENT_METHODS } from "@/lib/constants";
import { TransactionForm } from "./transaction-form";

export function TransactionHistory() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [categoryFilter, setCategoryFilter] = useState("all");
  const [periodFilter, setPeriodFilter] = useState("all");
  const [typeFilter, setTypeFilter] = useState("all");
  const [searchFilter, setSearchFilter] = useState("");
  const [paymentMethodFilter, setPaymentMethodFilter] = useState("all");
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage, setItemsPerPage] = useState(25);
  const [editingTransaction, setEditingTransaction] = useState(null);
  const [isEditDialogOpen, setIsEditDialogOpen] = useState(false);
  const [selectedTransactions, setSelectedTransactions] = useState<Set<string>>(new Set());
  const [isSelectionMode, setIsSelectionMode] = useState(false);
  const [isDeleteModalOpen, setIsDeleteModalOpen] = useState(false);
  const [deleteTarget, setDeleteTarget] = useState<{type: 'single' | 'multiple', id?: string}>({type: 'single'});

  const { data: categories } = useQuery({
    queryKey: ['/api/categories'],
    queryFn: async () => {
      const response = await fetch('/api/categories', {
        credentials: 'include',
      });
      if (!response.ok) throw new Error('Failed to fetch categories');
      return response.json();
    },
  });

  // Fun√ß√µes auxiliares definidas antes dos hooks que as usam
  const getCategoryName = (categoryId: string) => {
    if (!categoryId || !categories) return 'Sem categoria';
    const category = categories.find((c: any) => c.id === categoryId);
    return category?.name || 'Sem categoria';
  };

  const getPaymentMethodLabel = (method: string) => {
    const paymentMethod = PAYMENT_METHODS.find(p => p.value === method);
    return paymentMethod?.label || method;
  };

  const getFilterParams = () => {
    const params = new URLSearchParams();

    if (categoryFilter !== "all") {
      params.append("categoryId", categoryFilter);
    }

    if (typeFilter !== "all") {
      params.append("type", typeFilter);
    }

    if (paymentMethodFilter !== "all") {
      params.append("paymentMethod", paymentMethodFilter);
    }

    if (searchFilter.trim()) {
      params.append("search", searchFilter.trim());
    }

    // Add date filters based on period
    const now = new Date();
    let startDate, endDate;

    switch (periodFilter) {
      case "current-month":
        startDate = new Date(now.getFullYear(), now.getMonth(), 1);
        endDate = new Date(now.getFullYear(), now.getMonth() + 1, 0);
        break;
      case "last-3-months":
        startDate = new Date(now.getFullYear(), now.getMonth() - 2, 1);
        endDate = new Date(now.getFullYear(), now.getMonth() + 1, 0);
        break;
      case "current-year":
        startDate = new Date(now.getFullYear(), 0, 1);
        endDate = new Date(now.getFullYear(), 11, 31);
        break;
      case "all":
        // N√£o adiciona filtros de data - busca todas as transa√ß√µes
        startDate = null;
        endDate = null;
        break;
    }

    if (startDate && endDate) {
      params.append("startDate", startDate.toISOString());
      params.append("endDate", endDate.toISOString());
    }

    // Add pagination parameters
    params.append("limit", itemsPerPage.toString());
    params.append("offset", ((currentPage - 1) * itemsPerPage).toString());

    return params.toString();
  };

  const { data: transactionData, isLoading } = useQuery({
    queryKey: ['/api/transactions', categoryFilter, periodFilter, typeFilter, paymentMethodFilter, searchFilter, currentPage, itemsPerPage],
    queryFn: async () => {
      const filterParams = getFilterParams();
      const url = filterParams ? `/api/transactions?${filterParams}` : '/api/transactions';
      const response = await fetch(url, {
        credentials: 'include',
      });
      if (!response.ok) throw new Error('Failed to fetch transactions');
      return response.json();
    },
  });

  const transactions = transactionData?.transactions || [];
  const totalCount = transactionData?.totalCount || 0;
  const totalPages = Math.ceil(totalCount / itemsPerPage);

  // Reset page when filters change
  const resetPage = () => {
    if (currentPage > 1) {
      setCurrentPage(1);
    }
  };

  // Reset page whenever filter values change
  React.useEffect(() => {
    resetPage();
  }, [categoryFilter, typeFilter, periodFilter, paymentMethodFilter, searchFilter]);

  // Functions for pagination control
  const handleItemsPerPageChange = (value: string) => {
    setItemsPerPage(parseInt(value));
    setCurrentPage(1);
  };

  const handlePageChange = (page: number) => {
    setCurrentPage(page);
  };

  const deleteTransactionMutation = useMutation({
    mutationFn: async (id: string) => {
      await apiRequest('DELETE', `/api/transactions/${id}`, null);
    },
    onSuccess: () => {
      toast({
        variant: "success",
        title: "Sucesso",
        description: "Transa√ß√£o exclu√≠da com sucesso!",
      });
      queryClient.invalidateQueries({ queryKey: ['/api/transactions'] });
      queryClient.invalidateQueries({ queryKey: ['/api/transactions/recurring'] });
      queryClient.invalidateQueries({ queryKey: ['/api/transactions/future-commitments'] });
      queryClient.invalidateQueries({ queryKey: ['/api/financial-summary'] });
    },
    onError: (error) => {
      if (isUnauthorizedError(error as Error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Erro",
        description: "Falha ao excluir transa√ß√£o. Tente novamente.",
        variant: "destructive",
      });
    },
  });

  const deleteMultipleTransactionsMutation = useMutation({
    mutationFn: async (ids: string[]) => {
      await Promise.all(
        ids.map(id => apiRequest('DELETE', `/api/transactions/${id}`, null))
      );
    },
    onSuccess: () => {
      toast({
        variant: "success",
        title: "Sucesso",
        description: `${selectedTransactions.size} transa√ß√µes exclu√≠das com sucesso!`,
      });
      setSelectedTransactions(new Set());
      setIsSelectionMode(false);
      queryClient.invalidateQueries({ queryKey: ['/api/transactions'] });
      queryClient.invalidateQueries({ queryKey: ['/api/transactions/recurring'] });
      queryClient.invalidateQueries({ queryKey: ['/api/transactions/future-commitments'] });
      queryClient.invalidateQueries({ queryKey: ['/api/financial-summary'] });
    },
    onError: (error) => {
      if (isUnauthorizedError(error as Error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Erro",
        description: "Falha ao excluir transa√ß√µes. Tente novamente.",
        variant: "destructive",
      });
    },
  });

  const handleDelete = (id: string) => {
    setDeleteTarget({ type: 'single', id });
    setIsDeleteModalOpen(true);
  };

  const handleMultipleDelete = () => {
    if (selectedTransactions.size === 0) return;
    setDeleteTarget({ type: 'multiple' });
    setIsDeleteModalOpen(true);
  };

  const confirmDelete = () => {
    if (deleteTarget.type === 'single' && deleteTarget.id) {
      deleteTransactionMutation.mutate(deleteTarget.id);
    } else if (deleteTarget.type === 'multiple') {
      deleteMultipleTransactionsMutation.mutate(Array.from(selectedTransactions));
    }
    setIsDeleteModalOpen(false);
  };

  const cancelDelete = () => {
    setIsDeleteModalOpen(false);
    setDeleteTarget({ type: 'single' });
  };

  const toggleTransactionSelection = (id: string) => {
    const newSelection = new Set(selectedTransactions);
    if (newSelection.has(id)) {
      newSelection.delete(id);
    } else {
      newSelection.add(id);
    }
    setSelectedTransactions(newSelection);
  };

  const selectAllTransactions = () => {
    if (transactions) {
      setSelectedTransactions(new Set(transactions.map((t: any) => t.id)));
    }
  };

  const deselectAllTransactions = () => {
    setSelectedTransactions(new Set());
  };

  const cancelSelectionMode = () => {
    setIsSelectionMode(false);
    setSelectedTransactions(new Set());
  };

  const handleEdit = (transaction: any) => {
    setEditingTransaction(transaction);
    setIsEditDialogOpen(true);
  };

  const handleEditClose = () => {
    setEditingTransaction(null);
    setIsEditDialogOpen(false);
  };

  const handleExport = () => {
    if (!transactions || transactions.length === 0) {
      toast({
        title: "Nada para exportar",
        description: "N√£o h√° transa√ß√µes para exportar com os filtros aplicados.",
        variant: "destructive",
      });
      return;
    }

    try {
      // Criar cabe√ßalho CSV
      const headers = [
        'Data',
        'Tipo',
        'Descri√ß√£o',
        'Categoria',
        'Forma de Pagamento',
        'Valor'
      ];

      // Converter transa√ß√µes para CSV
      const csvData = transactions.map(transaction => [
        formatDate(transaction.date),
        transaction.type === 'income' ? 'Receita' : 'Despesa',
        transaction.description,
        getCategoryName(transaction.categoryId),
        getPaymentMethodLabel(transaction.paymentMethod),
        `"${formatCurrency(transaction.amount)}"`
      ]);

      // Combinar cabe√ßalho com dados
      const csvContent = [headers, ...csvData]
        .map(row => row.join(','))
        .join('\n');

      // Criar e baixar arquivo
      const blob = new Blob([csvContent], { type: 'text/csv;charset=utf-8;' });
      const link = document.createElement('a');

      if (link.download !== undefined) {
        const url = URL.createObjectURL(blob);
        link.setAttribute('href', url);

        // Nome do arquivo com data atual
        const today = new Date().toISOString().split('T')[0];
        const fileName = `transacoes_${today}.csv`;
        link.setAttribute('download', fileName);

        link.style.visibility = 'hidden';
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);

        toast({
          title: "Exporta√ß√£o conclu√≠da",
          description: `Arquivo ${fileName} baixado com sucesso!`,
        });
      }
    } catch (error) {
      console.error('Erro ao exportar:', error);
      toast({
        title: "Erro na exporta√ß√£o",
        description: "Falha ao gerar o arquivo. Tente novamente.",
        variant: "destructive",
      });
    }
  };

  const formatCurrency = (value: string) => {
    return new Intl.NumberFormat('pt-BR', {
      style: 'currency',
      currency: 'BRL',
    }).format(parseFloat(value));
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('pt-BR');
  };

  const clearAllFilters = () => {
    setCategoryFilter("all");
    setTypeFilter("all");
    setPeriodFilter("all");
    setSearchFilter("");
    setPaymentMethodFilter("all");
    setCurrentPage(1);
  };

  const hasActiveFilters = () => {
    return categoryFilter !== "all" || 
           typeFilter !== "all" || 
           periodFilter !== "all" || 
           searchFilter.trim() !== "" || 
           paymentMethodFilter !== "all";
  };

  return (
    <Card className="financial-card">
      <CardHeader>
        <div className="flex flex-col space-y-4">
          <div className="flex flex-col md:flex-row md:items-center md:justify-between">
            <CardTitle>Hist√≥rico de Transa√ß√µes</CardTitle>

            <div className="flex items-center space-x-2 mt-4 md:mt-0">
              {hasActiveFilters() && (
                <Button
                  variant="outline"
                  size="sm"
                  onClick={clearAllFilters}
                  className="text-orange-600 border-orange-300 hover:bg-orange-50"
                  data-testid="button-clear-filters"
                >
                  <FilterX className="w-4 h-4 mr-1" />
                  Limpar
                </Button>
              )}
            </div>
          </div>

          {/* Barra de busca */}
          <div className="relative max-w-md">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-4 h-4" />
            <Input
              type="text"
              placeholder="Buscar por nome, categoria ou m√©todo..."
              value={searchFilter}
              onChange={(e) => setSearchFilter(e.target.value)}
              className="pl-10 pr-4 py-2"
              data-testid="input-search-filter"
            />
          </div>

          {/* Filtros em linha */}
          <div className="flex flex-wrap gap-3">
            <Select value={categoryFilter} onValueChange={setCategoryFilter}>
              <SelectTrigger className="w-48" data-testid="select-category-filter">
                <SelectValue placeholder="Categoria" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">Todas as categorias</SelectItem>
                {categories?.map((category) => (
                  <SelectItem key={category.id} value={category.id}>
                    {category.name}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>

            <Select value={typeFilter} onValueChange={setTypeFilter}>
              <SelectTrigger className="w-32" data-testid="select-type-filter">
                <SelectValue placeholder="Tipo" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">Todos</SelectItem>
                <SelectItem value="income">Receitas</SelectItem>
                <SelectItem value="expense">Despesas</SelectItem>
              </SelectContent>
            </Select>

            <Select value={paymentMethodFilter} onValueChange={setPaymentMethodFilter}>
              <SelectTrigger className="w-40" data-testid="select-payment-filter">
                <SelectValue placeholder="Pagamento" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">Todos</SelectItem>
                {PAYMENT_METHODS.map((method) => (
                  <SelectItem key={method.value} value={method.value}>
                    {method.label}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>

            <Select value={periodFilter} onValueChange={setPeriodFilter}>
              <SelectTrigger className="w-40" data-testid="select-period-filter">
                <SelectValue placeholder="Todos os per√≠odos" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">Todos os per√≠odos</SelectItem>
                <SelectItem value="current-month">Este m√™s</SelectItem>
                <SelectItem value="last-3-months">√öltimos 3 meses</SelectItem>
                <SelectItem value="current-year">Este ano</SelectItem>
              </SelectContent>
            </Select>
          </div>

          {/* Configura√ß√µes de pagina√ß√£o e a√ß√µes */}
          <div className="flex flex-wrap gap-3 justify-between items-center">
            <div className="flex items-center gap-2">
              <span className="text-sm text-gray-600 dark:text-gray-400">Itens por p√°gina:</span>
              <Select value={itemsPerPage.toString()} onValueChange={handleItemsPerPageChange}>
                <SelectTrigger className="w-20" data-testid="select-items-per-page">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="25">25</SelectItem>
                  <SelectItem value="50">50</SelectItem>
                  <SelectItem value="100">100</SelectItem>
                </SelectContent>
              </Select>
            </div>

            <div className="flex gap-3">
            <Button 
              onClick={handleExport}
              className="bg-blue-500 hover:bg-blue-600 text-white"
              data-testid="button-export-transactions"
            >
              <Download className="w-4 h-4 mr-2" />
              Exportar
            </Button>

            {!isSelectionMode ? (
              <Button 
                onClick={() => setIsSelectionMode(true)}
                variant="outline"
                className="border-red-500 text-red-500 hover:bg-red-500 hover:text-white"
                data-testid="button-start-selection"
              >
                <Trash2 className="w-4 h-4 mr-2" />
                Excluir
              </Button>
            ) : (
              <div className="flex space-x-2">
                <Button 
                  onClick={handleMultipleDelete}
                  disabled={selectedTransactions.size === 0 || deleteMultipleTransactionsMutation.isPending}
                  className="bg-red-500 hover:bg-red-600 text-white"
                  data-testid="button-delete-selected"
                >
                  <Trash2 className="w-4 h-4 mr-2" />
                  Excluir ({selectedTransactions.size})
                </Button>
                <Button 
                  onClick={cancelSelectionMode}
                  variant="outline"
                  data-testid="button-cancel-selection"
                >
                  <X className="w-4 h-4 mr-2" />
                  Cancelar
                </Button>
              </div>
            )}
            </div>
          </div>
        </div>
      </CardHeader>

      <CardContent>
        {isLoading ? (
          <div className="space-y-4">
            {Array.from({ length: 10 }).map((_, i) => (
              <Skeleton key={i} className="h-16 w-full" />
            ))}
          </div>
        ) : !transactions || transactions.length === 0 ? (
          <div className="text-center py-8 text-gray-500 dark:text-gray-400">
            <Filter className="w-16 h-16 mx-auto mb-4 opacity-50" />
            <p className="text-lg font-medium mb-2">Nenhuma transa√ß√£o encontrada</p>
            <p className="text-sm mb-4">
              {hasActiveFilters() 
                ? 'Nenhuma transa√ß√£o corresponde aos filtros aplicados.' 
                : 'Adicione suas primeiras transa√ß√µes para come√ßar.'
              }
            </p>
            {hasActiveFilters() && (
              <Button
                variant="outline"
                onClick={clearAllFilters}
                className="text-orange-600 border-orange-300 hover:bg-orange-50"
              >
                <FilterX className="w-4 h-4 mr-2" />
                Limpar filtros
              </Button>
            )}
          </div>
        ) : (
          <div className="overflow-x-auto">
            <table className="w-full">
              <thead className="bg-gray-50 dark:bg-gray-700">
                <tr>
                  {isSelectionMode && (
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">
                      <div className="flex items-center space-x-2">
                        <Checkbox
                          checked={transactions && selectedTransactions.size === transactions.length && transactions.length > 0}
                          onCheckedChange={(checked) => {
                            if (checked) {
                              selectAllTransactions();
                            } else {
                              deselectAllTransactions();
                            }
                          }}
                          data-testid="checkbox-select-all"
                        />
                        <span>Todos</span>
                      </div>
                    </th>
                  )}
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">
                    Data
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">
                    Descri√ß√£o
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">
                    Categoria
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">
                    Forma
                  </th>
                  <th className="px-6 py-3 text-right text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">
                    Valor
                  </th>
                  <th className="px-6 py-3 text-right text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider">
                    A√ß√µes
                  </th>
                </tr>
              </thead>
              <tbody className="bg-white dark:bg-gray-800 divide-y divide-gray-200 dark:divide-gray-700">
                {transactions.map((transaction, index) => (
                  <tr key={transaction.id} className={`hover:bg-gray-50 dark:hover:bg-gray-700 ${
                    selectedTransactions.has(transaction.id) ? 'bg-blue-50 dark:bg-blue-900/20' : ''
                  }`} data-testid={`transaction-row-${index}`}>
                    {isSelectionMode && (
                      <td className="px-6 py-4 whitespace-nowrap">
                        <Checkbox
                          checked={selectedTransactions.has(transaction.id)}
                          onCheckedChange={() => toggleTransactionSelection(transaction.id)}
                          data-testid={`checkbox-transaction-${index}`}
                        />
                      </td>
                    )}
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900 dark:text-white">
                      {formatDate(transaction.date)}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div className="flex items-center">
                        <div className={`w-8 h-8 rounded-lg flex items-center justify-center mr-3 ${
                          transaction.type === 'income' 
                            ? 'bg-green-100 dark:bg-green-900 text-green-600' 
                            : 'bg-red-100 dark:bg-red-900 text-red-600'
                        }`}>
                          <i className={transaction.type === 'income' ? 'fas fa-arrow-up text-sm' : 'fas fa-arrow-down text-sm'}></i>
                        </div>
                        <span className="text-sm font-medium text-gray-900 dark:text-white" data-testid={`transaction-description-${index}`}>
                          {transaction.description}
                        </span>
                      </div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500 dark:text-gray-400">
                      {getCategoryName(transaction.categoryId)}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500 dark:text-gray-400">
                      {getPaymentMethodLabel(transaction.paymentMethod)}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium">
                      <span className={transaction.type === 'income' ? 'text-green-600' : 'text-red-600'} data-testid={`transaction-amount-${index}`}>
                        {transaction.type === 'income' ? '+' : '-'}{formatCurrency(transaction.amount)}
                      </span>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium">
                      <div className="flex justify-end space-x-2">
                        <Button
                          size="sm"
                          variant="ghost"
                          onClick={() => handleEdit(transaction)}
                          data-testid={`button-edit-transaction-${index}`}
                        >
                          <Edit className="w-4 h-4 text-blue-600" />
                        </Button>
                        <Button
                          size="sm"
                          variant="ghost"
                          onClick={() => handleDelete(transaction.id)}
                          disabled={deleteTransactionMutation.isPending}
                          data-testid={`button-delete-transaction-${index}`}
                        >
                          <Trash2 className="w-4 h-4 text-red-600" />
                        </Button>
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}

        {/* Pagina√ß√£o */}
        {totalCount > itemsPerPage && (
          <div className="flex flex-col sm:flex-row items-center justify-between gap-4 px-6 py-4 border-t border-gray-200 dark:border-gray-700">
            <div className="text-sm text-gray-600 dark:text-gray-400">
              Mostrando {Math.min((currentPage - 1) * itemsPerPage + 1, totalCount)} a {Math.min(currentPage * itemsPerPage, totalCount)} de {totalCount} transa√ß√µes
            </div>

            <div className="flex items-center gap-2">
              <Button
                variant="outline"
                size="sm"
                onClick={() => handlePageChange(currentPage - 1)}
                disabled={currentPage === 1}
                data-testid="button-previous-page"
              >
                Anterior
              </Button>

              <div className="flex items-center gap-1">
                {[...Array(Math.min(5, totalPages))].map((_, index) => {
                  let pageNumber;
                  if (totalPages <= 5) {
                    pageNumber = index + 1;
                  } else if (currentPage <= 3) {
                    pageNumber = index + 1;
                  } else if (currentPage >= totalPages - 2) {
                    pageNumber = totalPages - 4 + index;
                  } else {
                    pageNumber = currentPage - 2 + index;
                  }

                  return (
                    <Button
                      key={pageNumber}
                      variant={currentPage === pageNumber ? "default" : "outline"}
                      size="sm"
                      onClick={() => handlePageChange(pageNumber)}
                      className="w-8 h-8 p-0"
                      data-testid={`button-page-${pageNumber}`}
                    >
                      {pageNumber}
                    </Button>
                  );
                })}
              </div>

              <Button
                variant="outline"
                size="sm"
                onClick={() => handlePageChange(currentPage + 1)}
                disabled={currentPage === totalPages}
                data-testid="button-next-page"
              >
                Pr√≥xima
              </Button>
            </div>
          </div>
        )}
      </CardContent>

      {/* Edit Transaction Dialog */}
      <Dialog open={isEditDialogOpen} onOpenChange={setIsEditDialogOpen}>
        <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
          <DialogHeader>
            <DialogTitle>Editar Transa√ß√£o</DialogTitle>
            <DialogDescription>
              Modifique os dados da transa√ß√£o abaixo.
            </DialogDescription>
          </DialogHeader>
          {editingTransaction && (
            <TransactionForm 
              editingTransaction={editingTransaction}
              onSuccess={handleEditClose}
            />
          )}
        </DialogContent>
      </Dialog>

      {/* Modal de Confirma√ß√£o de Exclus√£o */}
      <Dialog open={isDeleteModalOpen} onOpenChange={setIsDeleteModalOpen}>
        <DialogContent className="max-w-md">
          <DialogHeader>
            <DialogTitle className="flex items-center text-red-600">
              <Trash2 className="w-5 h-5 mr-2" />
              Confirmar Exclus√£o
            </DialogTitle>
            <DialogDescription>
              {deleteTarget.type === 'single' 
                ? "Tem certeza que deseja excluir esta transa√ß√£o? Esta a√ß√£o n√£o pode ser desfeita."
                : `Tem certeza que deseja excluir ${selectedTransactions.size} transa√ß√£o${selectedTransactions.size !== 1 ? '√µes' : ''}? Esta a√ß√£o n√£o pode ser desfeita.`
              }
            </DialogDescription>
          </DialogHeader>

          <div className="flex justify-end space-x-3 mt-6">
            <Button 
              variant="outline" 
              onClick={cancelDelete}
              data-testid="button-cancel-delete"
            >
              <X className="w-4 h-4 mr-2" />
              Cancelar
            </Button>
            <Button 
              onClick={confirmDelete}
              disabled={deleteTransactionMutation.isPending || deleteMultipleTransactionsMutation.isPending}
              className="bg-red-500 hover:bg-red-600 text-white"
              data-testid="button-confirm-delete"
            >
              <Trash2 className="w-4 h-4 mr-2" />
              {deleteTarget.type === 'single' ? 'Excluir Transa√ß√£o' : `Excluir ${selectedTransactions.size}`}
            </Button>
          </div>
        </DialogContent>
      </Dialog>
    </Card>
  );
}

