import { useState } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useToast } from "@/hooks/use-toast";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { Target, Plus, Edit, Trash2, ChevronLeft, ChevronRight } from "lucide-react";
import { useAuth } from "@/hooks/useAuth";
import useEmblaCarousel from 'embla-carousel-react';

export function InvestmentGoals() {
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [isNewGoalDialogOpen, setIsNewGoalDialogOpen] = useState(false);
  const [editingGoal, setEditingGoal] = useState<any>(null);
  const [emblaRef, emblaApi] = useEmblaCarousel({ 
    align: 'start',
    containScroll: 'trimSnaps',
    slidesToScroll: 1,
    skipSnaps: false,
  });

  const currentMonth = new Date().getMonth() + 1;
  const currentYear = new Date().getFullYear();

  // Fetch budget goals for current month
  const { data: goals = [], isLoading } = useQuery({
    queryKey: ['/api/budget-goals', currentMonth, currentYear],
    queryFn: async () => {
      const response = await fetch(`/api/budget-goals?month=${currentMonth}&year=${currentYear}`, {
        credentials: 'include',
      });
      if (!response.ok) throw new Error('Failed to fetch goals');
      return response.json();
    },
  });

  // Create goal mutation
  const createGoalMutation = useMutation({
    mutationFn: async (goalData: any) => {
      return await apiRequest('POST', '/api/budget-goals', goalData);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/budget-goals"] });
      setIsNewGoalDialogOpen(false);
      toast({
        title: "Meta criada",
        description: "Sua meta de investimento foi criada com sucesso.",
      });
    },
    onError: () => {
      toast({
        title: "Erro",
        description: "Falha ao criar meta. Tente novamente.",
        variant: "destructive",
      });
    },
  });

  // Update goal mutation
  const updateGoalMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string; data: any }) => {
      return await apiRequest('PUT', `/api/budget-goals/${id}`, data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/budget-goals"] });
      setEditingGoal(null);
      toast({
        title: "Meta atualizada",
        description: "Sua meta foi atualizada com sucesso.",
      });
    },
    onError: () => {
      toast({
        title: "Erro",
        description: "Falha ao atualizar meta. Tente novamente.",
        variant: "destructive",
      });
    },
  });

  // Delete goal mutation
  const deleteGoalMutation = useMutation({
    mutationFn: async (id: string) => {
      return await apiRequest('DELETE', `/api/budget-goals/${id}`, null);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/budget-goals"] });
      toast({
        title: "Meta exclu√≠da",
        description: "A meta foi exclu√≠da com sucesso.",
      });
    },
    onError: () => {
      toast({
        title: "Erro",
        description: "Falha ao excluir meta. Tente novamente.",
        variant: "destructive",
      });
    },
  });

  const handleCreateGoal = (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();
    const formData = new FormData(event.currentTarget);

    const goalData = {
      targetAmount: formData.get("targetAmount") as string, // Keep as string for decimal field
      month: currentMonth,
      year: currentYear,
      categoryId: null, // Investment goals don't have specific category
    };

    createGoalMutation.mutate(goalData);
  };

  const handleUpdateGoal = (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();
    const formData = new FormData(event.currentTarget);

    const goalData = {
      targetAmount: formData.get("targetAmount") as string, // Keep as string for decimal field
    };

    updateGoalMutation.mutate({ id: editingGoal.id, data: goalData });
  };

  const formatCurrency = (value: number) => {
    return new Intl.NumberFormat('pt-BR', {
      style: 'currency',
      currency: 'BRL',
    }).format(value);
  };

  const investmentGoals = goals.filter(goal => !goal.categoryId); // Investment goals without specific category

  // Fetch current investments to calculate real progress
  const { data: investments = [] } = useQuery({
    queryKey: ['/api/investments'],
    queryFn: async () => {
      const response = await fetch('/api/investments', {
        credentials: 'include',
      });
      if (!response.ok) throw new Error('Failed to fetch investments');
      return response.json();
    },
  });

  const scrollPrev = () => {
    if (emblaApi) emblaApi.scrollPrev();
  };

  const scrollNext = () => {
    if (emblaApi) emblaApi.scrollNext();
  };

  return (
    <Card className="financial-card">
      <CardHeader className="flex flex-row items-center justify-between">
        <CardTitle className="flex items-center">
          <Target className="w-5 h-5 mr-2" />
          Metas de Investimento
        </CardTitle>

        <Dialog open={isNewGoalDialogOpen} onOpenChange={setIsNewGoalDialogOpen}>
          <DialogTrigger asChild>
            <Button size="sm" data-testid="button-add-investment-goal">
              <Plus className="w-4 h-4 mr-2" />
              Nova Meta
            </Button>
          </DialogTrigger>
          <DialogContent>
            <DialogHeader>
              <DialogTitle>Criar Meta de Investimento</DialogTitle>
            </DialogHeader>
            <form onSubmit={handleCreateGoal} className="space-y-4">
              <div>
                <Label htmlFor="targetAmount">Valor da Meta (R$)</Label>
                <Input
                  id="targetAmount"
                  name="targetAmount"
                  type="number"
                  step="0.01"
                  min="0.01"
                  placeholder="1000.00"
                  required
                  data-testid="input-goal-amount"
                />
                <p className="text-xs text-gray-500 mt-1">
                  Digite o valor que deseja investir este m√™s
                </p>
              </div>
              <div className="flex justify-end space-x-2">
                <Button type="button" variant="outline" onClick={() => setIsNewGoalDialogOpen(false)}>
                  Cancelar
                </Button>
                <Button type="submit" disabled={createGoalMutation.isPending} data-testid="button-create-goal">
                  {createGoalMutation.isPending ? "Criando..." : "Criar Meta"}
                </Button>
              </div>
            </form>
          </DialogContent>
        </Dialog>
      </CardHeader>

      <CardContent>
        {isLoading ? (
          <div className="animate-pulse space-y-4">
            <div className="h-4 bg-gray-200 dark:bg-gray-700 rounded w-3/4"></div>
            <div className="h-2 bg-gray-200 dark:bg-gray-700 rounded"></div>
          </div>
        ) : investmentGoals.length === 0 ? (
          <div className="text-center py-8">
            <Target className="w-12 h-12 text-gray-400 mx-auto mb-4" />
            <p className="text-gray-600 dark:text-gray-400 mb-4">
              Nenhuma meta de investimento definida para este m√™s
            </p>
            <Button
              onClick={() => setIsNewGoalDialogOpen(true)}
              variant="outline"
              data-testid="button-create-first-goal"
            >
              <Plus className="w-4 h-4 mr-2" />
              Criar primeira meta
            </Button>
          </div>
        ) : (
          <div className="relative">
            {/* Navigation Buttons */}
            {investmentGoals.length > 1 && (
              <>
                <Button
                  variant="outline"
                  size="sm"
                  className="absolute left-2 top-1/2 -translate-y-1/2 z-20 bg-white dark:bg-gray-800 shadow-lg border-2 hover:bg-gray-50 dark:hover:bg-gray-700"
                  onClick={scrollPrev}
                  data-testid="button-carousel-prev"
                >
                  <ChevronLeft className="w-4 h-4" />
                </Button>
                <Button
                  variant="outline"
                  size="sm"
                  className="absolute right-2 top-1/2 -translate-y-1/2 z-20 bg-white dark:bg-gray-800 shadow-lg border-2 hover:bg-gray-50 dark:hover:bg-gray-700"
                  onClick={scrollNext}
                  data-testid="button-carousel-next"
                >
                  <ChevronRight className="w-4 h-4" />
                </Button>
              </>
            )}

            {/* Carousel Container */}
            <div ref={emblaRef} className="overflow-hidden px-8" data-testid="goals-carousel">
              <div className="flex gap-4">
                {investmentGoals.map((goal) => {
                  const targetAmount = parseFloat(goal.targetAmount);
                  // Calculate current amount from actual investments
                  const currentAmount = investments.reduce((total: number, investment: any) => {
                    return total + parseFloat(investment.currentAmount || 0);
                  }, 0);
                  const percentage = Math.min((currentAmount / targetAmount) * 100, 100);

                  return (
                    <div key={goal.id} className="flex-none w-72 min-w-0 border border-gray-200 dark:border-gray-700 rounded-lg p-4 bg-white dark:bg-gray-800 shadow-sm" data-testid={`goal-card-${goal.id}`}>
                      <div className="flex items-center justify-between mb-4">
                        <h4 className="font-semibold text-gray-900 dark:text-white text-sm">
                          Meta Mensal de Investimento
                        </h4>
                        <div className="flex items-center space-x-1">
                          <Dialog open={editingGoal?.id === goal.id} onOpenChange={(open) => setEditingGoal(open ? goal : null)}>
                            <DialogTrigger asChild>
                              <Button variant="outline" size="sm" data-testid={`button-edit-goal-${goal.id}`}>
                                <Edit className="w-3 h-3" />
                              </Button>
                            </DialogTrigger>
                            <DialogContent>
                              <DialogHeader>
                                <DialogTitle>Editar Meta de Investimento</DialogTitle>
                              </DialogHeader>
                              <form onSubmit={handleUpdateGoal} className="space-y-4">
                                <div>
                                  <Label htmlFor="editTargetAmount">Valor da Meta</Label>
                                  <Input
                                    id="editTargetAmount"
                                    name="targetAmount"
                                    type="number"
                                    step="0.01"
                                    defaultValue={goal.targetAmount}
                                    required
                                    data-testid="input-edit-goal-amount"
                                  />
                                </div>
                                <div className="flex justify-end space-x-2">
                                  <Button type="button" variant="outline" onClick={() => setEditingGoal(null)}>
                                    Cancelar
                                  </Button>
                                  <Button type="submit" disabled={updateGoalMutation.isPending}>
                                    {updateGoalMutation.isPending ? "Salvando..." : "Salvar"}
                                  </Button>
                                </div>
                              </form>
                            </DialogContent>
                          </Dialog>

                          <Button
                            variant="outline"
                            size="sm"
                            onClick={() => deleteGoalMutation.mutate(goal.id)}
                            disabled={deleteGoalMutation.isPending}
                            data-testid={`button-delete-goal-${goal.id}`}
                          >
                            <Trash2 className="w-3 h-3" />
                          </Button>
                        </div>
                      </div>

                      <div className="mb-4">
                        <div className="flex justify-between text-xs mb-2">
                          <span className="text-gray-600 dark:text-gray-400">
                            {formatCurrency(currentAmount)} / {formatCurrency(targetAmount)}
                          </span>
                          <span className="text-gray-600 dark:text-gray-400">{Math.round(percentage)}%</span>
                        </div>
                        <div className="w-full bg-gray-200 dark:bg-gray-600 rounded-full h-2">
                          <div 
                            className="bg-blue-500 h-2 rounded-full transition-all duration-300" 
                            style={{ width: `${percentage}%` }}
                          ></div>
                        </div>
                      </div>

                      <p className="text-xs text-gray-500 dark:text-gray-400">
                        {currentAmount >= targetAmount 
                          ? "üéâ Meta atingida!" 
                          : `Faltam ${formatCurrency(targetAmount - currentAmount)} para atingir sua meta`
                        }
                      </p>
                    </div>
                  );
                })}
              </div>
            </div>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

