import { useState } from "react";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { Sidebar } from "@/components/layout/sidebar";
import { Header } from "@/components/layout/header";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Progress } from "@/components/ui/progress";
import { Badge } from "@/components/ui/badge";
import { Upload, FileText, Brain, CheckCircle, AlertCircle, Download, Check, X, Zap, Star } from "lucide-react";
import { Checkbox } from "@/components/ui/checkbox";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";

interface ParsedTransaction {
  date: string;
  description: string;
  amount: number;
  type: 'income' | 'expense';
  category: string;
  confidence: number;
  isSubscription?: boolean;
}

interface DetectedSubscription {
  merchant: string;
  amount: string;
  confidence: number;
  category: string;
  description: string;
}

export default function Import() {
  const { user, isAuthenticated } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [extractText, setExtractText] = useState("");
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [parsedTransactions, setParsedTransactions] = useState<ParsedTransaction[]>([]);
  const [selectedTransactions, setSelectedTransactions] = useState<Set<number>>(new Set());
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [currentStep, setCurrentStep] = useState(1);
  const [analysisProgress, setAnalysisProgress] = useState(0);
  const [progressMessage, setProgressMessage] = useState("");
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [autoProcessing, setAutoProcessing] = useState(false);
  const [detectedSubscriptions, setDetectedSubscriptions] = useState<DetectedSubscription[]>([]);

  // Fetch categories for mapping
  const { data: categories = [] } = useQuery({
    queryKey: ['/api/categories'],
    queryFn: async () => {
      const response = await fetch('/api/categories', {
        credentials: 'include',
      });
      if (!response.ok) throw new Error('Failed to fetch categories');
      return response.json();
    },
  });

  // Import transactions mutation with auto subscription detection
  const importTransactionsMutation = useMutation({
    mutationFn: async (transactions: any[]) => {
      const promises = transactions.map(transaction =>
        fetch("/api/transactions", {
          method: "POST",
          credentials: 'include',
          body: JSON.stringify(transaction),
          headers: { "Content-Type": "application/json" },
        }).then(res => {
          if (!res.ok) throw new Error(`Failed to import transaction: ${res.status}`);
          return res.json();
        })
      );

      const results = await Promise.all(promises);

      // Trigger automatic subscription detection after importing all transactions
      try {
        await fetch("/api/subscriptions/detect", {
          method: "POST",
          credentials: 'include',
          headers: { "Content-Type": "application/json" }
        });
        console.log("Auto-detection of subscriptions triggered after import");
      } catch (error) {
        console.warn("Failed to auto-detect subscriptions:", error);
      }

      return results;
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/transactions"] });
      queryClient.invalidateQueries({ queryKey: ["/api/subscriptions"] });
      queryClient.invalidateQueries({ queryKey: ["/api/financial-summary"] });
      toast({
        title: "Transa√ß√µes importadas",
        description: `${selectedTransactions.size} transa√ß√µes importadas com sucesso. Verificando automaticamente por assinaturas...`,
      });
      setCurrentStep(1);
      setParsedTransactions([]);
      setSelectedTransactions(new Set());
      setExtractText("");
      setSelectedFile(null);
    },
    onError: () => {
      toast({
        title: "Erro na importa√ß√£o",
        description: "Falha ao importar transa√ß√µes. Tente novamente.",
        variant: "destructive",
      });
    },
  });

  const handleFileSelect = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      // Validate file type
      const allowedTypes = ['text/plain', 'text/csv', 'application/pdf'];
      if (!allowedTypes.includes(file.type)) {
        toast({
          title: "Formato n√£o suportado",
          description: "Por favor, envie arquivos PDF, TXT ou CSV.",
          variant: "destructive",
        });
        return;
      }

      setSelectedFile(file);

      if (file.type === 'application/pdf') {
        toast({
          title: "PDF selecionado",
          description: "Clique em 'Carregar e Analisar Arquivo' para processar o PDF.",
        });
      }
    }
  };

  const handleFileUpload = async () => {
    if (!selectedFile) return;

    // For text files, read content directly
    if (selectedFile.type === 'text/plain' || selectedFile.type === 'text/csv') {
      const reader = new FileReader();
      reader.onload = (e) => {
        const content = e.target?.result as string;
        setExtractText(content);

        toast({
          title: "Arquivo carregado",
          description: "Iniciando an√°lise autom√°tica do extrato...",
        });

        // Automatically open modal and analyze the extract
        setTimeout(() => {
          if (content.trim()) {
            setIsModalOpen(true);
            setAutoProcessing(true);
            analyzeExtractWithAI();
          }
        }, 500); // Small delay to ensure state is updated
      };
      reader.readAsText(selectedFile);
    } 
    // For PDF files, send to server for progressive text extraction
    else if (selectedFile.type === 'application/pdf') {
      setIsAnalyzing(true);
      setCurrentStep(2);
      setAnalysisProgress(0);
      setProgressMessage("Iniciando processamento do PDF...");

      const formData = new FormData();
      formData.append('file', selectedFile);

      try {
        const response = await fetch('/api/extract-pdf-text', {
          method: 'POST',
          credentials: 'include',
          body: formData
        });

        if (!response.ok) {
          throw new Error('Falha ao processar PDF');
        }

        const result = await response.json();
        const sessionId = result.sessionId;

        // Poll for progress
        const pollProgress = async () => {
          try {
            const progressResponse = await fetch(`/api/pdf-progress/${sessionId}`, {
              credentials: 'include'
            });

            if (!progressResponse.ok) {
              throw new Error('Falha ao obter progresso');
            }

            const progressData = await progressResponse.json();
            setAnalysisProgress(progressData.progress);
            setProgressMessage(progressData.message);

            if (progressData.status === 'completed') {
              // Get final result
              const resultResponse = await fetch(`/api/pdf-result/${sessionId}`, {
                credentials: 'include'
              });

              if (resultResponse.ok) {
                const finalResult = await resultResponse.json();
                setExtractText(finalResult.text);

                toast({
                  title: "PDF processado com sucesso!",
                  description: `${finalResult.text.length} caracteres extra√≠dos de ${finalResult.pages} p√°ginas.`,
                });

                // Reset states
                setIsAnalyzing(false);
                setCurrentStep(1);

                // Automatically open modal and start analysis
                setTimeout(() => {
                  if (finalResult.text.trim()) {
                    setIsModalOpen(true);
                    setAutoProcessing(true);

                    // Small delay to ensure modal is rendered and state is updated
                    setTimeout(() => {
                      analyzeExtractWithAIWithText(finalResult.text);
                    }, 500);
                  }
                }, 1000);
              }

              return; // Stop polling
            } else if (progressData.status === 'error') {
              throw new Error(progressData.message);
            }

            // Continue polling
            setTimeout(pollProgress, 2000);

          } catch (error) {
            console.error('Error polling progress:', error);
            toast({
              title: "Erro no processamento",
              description: "Falha durante o processamento do PDF.",
              variant: "destructive",
            });
            setIsAnalyzing(false);
            setCurrentStep(1);
          }
        };

        // Set analysis state and step to show progress
        setCurrentStep(2);
        setAnalysisProgress(10);
        setProgressMessage("Iniciando processamento do PDF...");

        // Start polling
        setTimeout(pollProgress, 1000);

      } catch (error) {
        toast({
          title: "Erro ao processar PDF",
          description: "N√£o foi poss√≠vel iniciar o processamento. Tente novamente.",
          variant: "destructive",
        });
        setIsAnalyzing(false);
        setCurrentStep(1);
      }
    }
  };

  // Function to connect to SSE for progress tracking
  const connectToProgressStream = (sessionId: string) => {
    const eventSource = new EventSource(`/api/analyze-extract-progress/${sessionId}`);

    eventSource.onmessage = (event) => {
      try {
        const data = JSON.parse(event.data);
        setAnalysisProgress(data.progress);
        setProgressMessage(data.message);
      } catch (error) {
        console.error("Error parsing SSE data:", error);
      }
    };

    eventSource.onerror = (error) => {
      console.error("SSE connection error:", error);
      eventSource.close();
    };

    return eventSource;
  };

  // Version that accepts text parameter to avoid race conditions
  const analyzeExtractWithAIWithText = async (textToAnalyze: string) => {
    if (!textToAnalyze.trim()) {
      toast({
        title: "Texto vazio",
        description: "Por favor, adicione o texto do extrato banc√°rio.",
        variant: "destructive",
      });
      return;
    }

    // Call the main function with the provided text
    await analyzeExtractWithAI(textToAnalyze);
  };

  const analyzeExtractWithAI = async (textToAnalyze?: string) => {
    const textToUse = textToAnalyze || extractText;

    if (!textToUse.trim()) {
      toast({
        title: "Texto vazio",
        description: "Por favor, adicione o texto do extrato banc√°rio.",
        variant: "destructive",
      });
      return;
    }

    setIsAnalyzing(true);
    setCurrentStep(2);
    setAnalysisProgress(0);
    setProgressMessage("Iniciando an√°lise...");

    // Generate unique session ID for progress tracking
    const sessionId = `session_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;

    // Connect to progress stream
    const eventSource = connectToProgressStream(sessionId);

    try {
      // Call OpenAI API to analyze the bank statement
      const response = await fetch("/api/analyze-extract", {
        method: "POST",
        credentials: 'include',
        body: JSON.stringify({ 
          extractText: textToUse,
          availableCategories: categories.map((cat: any) => cat.name),
          sessionId
        }),
        headers: { "Content-Type": "application/json" },
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const result = await response.json();
      const analyzedTransactions = result.transactions || [];
      const subscriptions = result.detectedSubscriptions || [];

      // Always ensure at least one transaction for user feedback
      if (analyzedTransactions.length === 0) {
        // Create a placeholder transaction that user can edit
        analyzedTransactions.push({
          date: "2025-01-01",
          description: "Nenhuma transa√ß√£o autom√°tica foi detectada - edite este exemplo",
          amount: -100.00,
          type: "expense",
          category: "Outros"
        });
      }

      // Transactions received successfully from AI analysis
      setParsedTransactions(analyzedTransactions);
      setDetectedSubscriptions(subscriptions);
      // Seleciona todas as transa√ß√µes por padr√£o
      setSelectedTransactions(new Set(Array.from({ length: analyzedTransactions.length }, (_, i) => i)));
      setCurrentStep(3);

      const subscriptionCount = subscriptions.length;
      const variant = analyzedTransactions.length > 0 ? "success" : "warning";
      toast({
        variant,
        title: "An√°lise conclu√≠da",
        description: `${analyzedTransactions.length} transa√ß√µes identificadas${subscriptionCount > 0 ? ` e ${subscriptionCount} poss√≠vel${subscriptionCount > 1 ? 'is' : ''} assinatura${subscriptionCount > 1 ? 's' : ''} detectada${subscriptionCount > 1 ? 's' : ''}` : ''}.`,
      });
    } catch (error) {
      toast({
        title: "Erro na an√°lise",
        description: "Falha ao analisar o extrato. Verifique o formato e tente novamente.",
        variant: "destructive",
      });
      setCurrentStep(1);
    } finally {
      eventSource.close();
      setIsAnalyzing(false);
      setAnalysisProgress(0);
      setProgressMessage("");
      setAutoProcessing(false);
    }
  };

  // Fun√ß√µes para gerenciar sele√ß√£o de transa√ß√µes
  const toggleTransaction = (index: number) => {
    const newSelected = new Set(selectedTransactions);
    if (newSelected.has(index)) {
      newSelected.delete(index);
    } else {
      newSelected.add(index);
    }
    setSelectedTransactions(newSelected);
  };

  const selectAll = () => {
    setSelectedTransactions(new Set(Array.from({ length: parsedTransactions.length }, (_, i) => i)));
  };

  const deselectAll = () => {
    setSelectedTransactions(new Set());
  };

  const handleImportTransactions = () => {
    // Filtra apenas as transa√ß√µes selecionadas
    const selectedParsedTransactions = parsedTransactions.filter((_, index) => 
      selectedTransactions.has(index)
    );

    const transactionsToImport = selectedParsedTransactions.map(transaction => {
      // Ensure we have valid data
      const amount = transaction.amount ? Math.abs(Number(transaction.amount)) : 0;
      const date = transaction.date || "2024-12-10";
      const description = transaction.description || "Transa√ß√£o importada";
      const type = transaction.type || "expense";

      return {
        amount: amount.toString(),
        type: type,
        description: description,
        date: date,
        categoryId: categories.find((cat: any) => 
          cat.name.toLowerCase().includes((transaction.category || "outros").toLowerCase())
        )?.id || null,
        paymentMethod: "transfer"
      };
    });

    importTransactionsMutation.mutate(transactionsToImport);
  };

  const formatCurrency = (value: number) => {
    return new Intl.NumberFormat('pt-BR', {
      style: 'currency',
      currency: 'BRL',
    }).format(value);
  };

  const getStepProgress = () => {
    return (currentStep / 3) * 100;
  };

  if (!isAuthenticated) {
    return <div className="flex h-screen items-center justify-center">Carregando...</div>;
  }

  return (
    <div className="flex h-screen bg-gray-50 dark:bg-gray-900">
      <Sidebar />

      <main className="flex-1 overflow-auto">
        <Header 
          title="Importa√ß√£o de Extratos" 
          subtitle="Importe seus extratos banc√°rios com an√°lise inteligente" 
        />

        <div className="p-6 space-y-6">
          {/* Progress Bar */}
          <Card className="financial-card">
            <CardContent className="p-6">
              <div className="space-y-4">
                <div className="flex justify-between text-sm">
                  <span className={currentStep >= 1 ? "text-blue-600 font-medium" : "text-gray-500"}>
                    1. Upload do Extrato
                  </span>
                  <span className={currentStep >= 2 ? "text-blue-600 font-medium" : "text-gray-500"}>
                    2. An√°lise IA
                  </span>
                  <span className={currentStep >= 3 ? "text-blue-600 font-medium" : "text-gray-500"}>
                    3. Revis√£o e Importa√ß√£o
                  </span>
                </div>
                <Progress value={getStepProgress()} className="h-2 transition-all duration-300 ease-out" />
              </div>
            </CardContent>
          </Card>

          {/* Step 1: Upload */}
          {currentStep === 1 && (
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
              <Card className="financial-card">
                <CardHeader>
