              <Card 
                key={plan.name}
                className={`financial-card relative ${
                  plan.popular ? 'ring-2 ring-blue-500 dark:ring-blue-400' : ''
                } ${plan.color === 'purple' ? 'border-purple-200 dark:border-purple-800' : ''}`}
              >
