              )}
              {filters.amountRange.max && (
                <Badge variant="outline">
                  Max: R$ {filters.amountRange.max}
                </Badge>
              )}
              {filters.categories.length > 0 && (
                <Badge variant="outline">
                  {filters.categories.length} categoria(s)
                </Badge>
              )}
            </div>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

