import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Skeleton } from "@/components/ui/skeleton";
import { Clock, CreditCard, FileText, Calendar, RefreshCw, ChevronDown, X } from "lucide-react";
import { useState } from "react";

interface FutureCommitment {
  id: string;
  description: string;
  totalValue: string | null;
  totalInstallments: number | null;
  paidInstallments: number | null;
  installmentValue: string;
  paymentMethod: string;
  categoryName: string;
  type: 'installment' | 'monthly';
  dueDay?: number;
}

export function FutureCommitments() {
  const [isDialogOpen, setIsDialogOpen] = useState(false);

  const { data: commitments = [], isLoading, refetch } = useQuery({
    queryKey: ['/api/transactions/future-commitments'],
    queryFn: async (): Promise<FutureCommitment[]> => {
      const response = await fetch('/api/transactions/future-commitments', {
        credentials: 'include',
      });
      if (!response.ok) throw new Error('Failed to fetch future commitments');
      return await response.json() as FutureCommitment[];
    },
    staleTime: 0,
    refetchOnWindowFocus: true,
  });

  const formatCurrency = (value: string) => {
    return new Intl.NumberFormat('pt-BR', {
      style: 'currency',
      currency: 'BRL',
    }).format(parseFloat(value));
  };

  const getPaymentMethodIcon = (method: string, type: string) => {
    if (type === 'monthly') {
      return <RefreshCw className="w-4 h-4" />;
    }

    switch (method) {
      case 'credit_card':
        return <CreditCard className="w-4 h-4" />;
      case 'other':
        return <FileText className="w-4 h-4" />;
      default:
        return <Clock className="w-4 h-4" />;
    }
  };

  const getPaymentMethodLabel = (method: string, type: string) => {
    if (type === 'monthly') {
      return 'Conta Fixa';
    }

    switch (method) {
      case 'credit_card':
        return 'Cart√£o de Cr√©dito';
      case 'other':
        return 'Financiamento';
      default:
        return method;
    }
  };

  if (isLoading) {
    return (
      <Card className="financial-card">
        <CardHeader>
          <div className="flex items-center gap-2">
            <Clock className="w-5 h-5" />
            <CardTitle>Compromissos Futuros</CardTitle>
          </div>
        </CardHeader>
        <CardContent>
          <div className="space-y-4">
            {Array.from({ length: 3 }).map((_, i) => (
              <Skeleton key={i} className="h-20 w-full" />
            ))}
          </div>
        </CardContent>
      </Card>
    );
  }

  if (!commitments || commitments.length === 0) {
    return (
      <Card className="financial-card">
        <CardHeader>
          <div className="flex items-center gap-2">
            <Clock className="w-5 h-5 text-blue-600" />
            <CardTitle>Compromissos Futuros</CardTitle>
          </div>
        </CardHeader>
        <CardContent>
          <div className="text-center py-8 text-gray-500 dark:text-gray-400">
            <Clock className="w-16 h-16 mx-auto mb-4 opacity-50" />
            <p>Nenhum compromisso futuro encontrado.</p>
            <p className="text-sm">Compras parceladas aparecer√£o aqui.</p>
          </div>
        </CardContent>
      </Card>
    );
  }

  // Get current month and next months dynamically  
  const now = new Date();
  const currentMonth = now.getMonth();
  const currentYear = now.getFullYear();

  // Get month names in Portuguese
  const getMonthName = (month: number, year: number) => {
    return new Date(year, month).toLocaleDateString('pt-BR', { month: 'long' });
  };

  const currentMonthName = getMonthName(currentMonth, currentYear);
  const nextMonthName = getMonthName(currentMonth + 1, currentMonth === 11 ? currentYear + 1 : currentYear);
  const thirdMonthName = getMonthName(currentMonth + 2, currentMonth >= 10 ? currentYear + 1 : currentYear);

  const currentMonthCommitments: FutureCommitment[] = [];
  const nextMonthCommitments: FutureCommitment[] = [];
  const thirdMonthCommitments: FutureCommitment[] = [];

  commitments.forEach(commitment => {
    if (commitment.type === 'monthly') {
      // Fixed expenses repeat monthly - add to all periods
      currentMonthCommitments.push(commitment);
      nextMonthCommitments.push(commitment);
      thirdMonthCommitments.push(commitment);
    } else {
      // Installment commitments - check remaining payments
      const remainingInstallments = commitment.totalInstallments! - commitment.paidInstallments!;
      if (remainingInstallments > 0) {
        currentMonthCommitments.push(commitment);
        if (remainingInstallments > 1) {
          nextMonthCommitments.push(commitment);
          if (remainingInstallments > 2) {
            thirdMonthCommitments.push(commitment);
          }
        }
      }
    }
  });

  const calculateMonthTotal = (commitmentList: FutureCommitment[]) => {
    return commitmentList.reduce((total, commitment) => {
      if (commitment.type === 'monthly') {
        return total + parseFloat(commitment.installmentValue);
      } else {
        // Only count 1 installment per month
        return total + parseFloat(commitment.installmentValue);
      }
    }, 0);
  };

  const currentMonthTotal = calculateMonthTotal(currentMonthCommitments);
  const nextMonthTotal = calculateMonthTotal(nextMonthCommitments);
  const thirdMonthTotal = calculateMonthTotal(thirdMonthCommitments);
  const totalOutstanding = currentMonthTotal + nextMonthTotal + thirdMonthTotal;

  return (
    <Card className="financial-card">
      <CardHeader>
        <div className="flex items-center justify-between">
          <div className="flex items-center gap-2">
            <Clock className="w-5 h-5 text-orange-600" />
            <CardTitle>Compromissos Futuros</CardTitle>
          </div>
          <div className="text-right">
            <p className="text-xs text-gray-500 dark:text-gray-400">Total pendente</p>
            <p className="text-lg font-bold text-orange-600" data-testid="total-outstanding">
              {formatCurrency(totalOutstanding.toString())}
            </p>
          </div>
        </div>
      </CardHeader>

      <CardContent className="space-y-6">
        {/* Current Month */}
        <div className="p-4 rounded-lg bg-blue-50 dark:bg-blue-900/20 border border-blue-200 dark:border-blue-800 text-center">
          <div className="flex items-center justify-center gap-2 mb-3">
            <Calendar className="w-4 h-4 text-blue-600" />
            <h3 className="text-base font-semibold text-gray-900 dark:text-white capitalize">{currentMonthName}</h3>
          </div>
          <p className="text-lg font-bold text-blue-600 mb-1">
            {formatCurrency(currentMonthTotal.toString())}
          </p>
          <p className="text-xs text-gray-600 dark:text-gray-400">{currentMonthCommitments.length} compromisso{currentMonthCommitments.length !== 1 ? 's' : ''}</p>
        </div>

        {/* Next Month */}
        <div className="p-4 rounded-lg bg-purple-50 dark:bg-purple-900/20 border border-purple-200 dark:border-purple-800 text-center">
          <div className="flex items-center justify-center gap-2 mb-3">
            <Clock className="w-4 h-4 text-purple-600" />
            <h3 className="text-base font-semibold text-gray-900 dark:text-white capitalize">{nextMonthName}</h3>
          </div>
          <p className="text-lg font-bold text-purple-600 mb-1">
            {formatCurrency(nextMonthTotal.toString())}
          </p>
          <p className="text-xs text-gray-600 dark:text-gray-400">{nextMonthCommitments.length} compromisso{nextMonthCommitments.length !== 1 ? 's' : ''}</p>
        </div>

        {/* Third Month */}
        <div className="p-4 rounded-lg bg-green-50 dark:bg-green-900/20 border border-green-200 dark:border-green-800 text-center">
          <div className="flex items-center justify-center gap-2 mb-3">
            <Clock className="w-4 h-4 text-green-600" />
            <h3 className="text-base font-semibold text-gray-900 dark:text-white capitalize">{thirdMonthName}</h3>
          </div>
          <p className="text-lg font-bold text-green-600 mb-1">
            {formatCurrency(thirdMonthTotal.toString())}
          </p>
          <p className="text-xs text-gray-600 dark:text-gray-400">{thirdMonthCommitments.length} compromisso{thirdMonthCommitments.length !== 1 ? 's' : ''}</p>
        </div>

        {/* Ver Compromissos Button */}
        <div className="flex justify-center">
          <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
            <DialogTrigger asChild>
              <Button
                variant="outline"
                className="flex items-center gap-2"
                data-testid="show-commitments-details"
              >
                <ChevronDown className="w-4 h-4" />
                Ver Compromissos
              </Button>
            </DialogTrigger>
            <DialogContent className="max-w-2xl max-h-[80vh] overflow-y-auto">
              <DialogHeader>
                <DialogTitle className="flex items-center gap-2">
                  <Clock className="w-5 h-5 text-orange-600" />
                  Compromissos dos Pr√≥ximos 3 Meses
                </DialogTitle>
              </DialogHeader>

              <div className="space-y-6 mt-4">
                {/* Current Month Details */}
                <div className="space-y-3">
                  <div className="flex items-center justify-between p-3 rounded-lg bg-blue-50 dark:bg-blue-900/20 border border-blue-200 dark:border-blue-800">
                    <div className="flex items-center gap-3">
                      <Calendar className="w-5 h-5 text-blue-600" />
                      <div>
                        <h3 className="font-semibold text-gray-900 dark:text-white capitalize">{currentMonthName}</h3>
                        <p className="text-xs text-gray-600 dark:text-gray-400">{currentMonthCommitments.length} compromisso{currentMonthCommitments.length !== 1 ? 's' : ''}</p>
                      </div>
                    </div>
                    <p className="text-lg font-bold text-blue-600">
                      {formatCurrency(currentMonthTotal.toString())}
                    </p>
                  </div>

                  <div className="grid gap-2 ml-8">
                    {currentMonthCommitments.map((commitment, index) => (
                      <div
                        key={`current-${commitment.type}-${commitment.id}`}
                        className="flex items-center justify-between p-2 rounded border bg-white dark:bg-gray-800 text-sm"
                        data-testid={`current-commitment-${index}`}
                      >
                        <div className="flex items-center gap-2">
                          {getPaymentMethodIcon(commitment.paymentMethod, commitment.type)}
                          <span className="text-gray-900 dark:text-white truncate max-w-[200px]">
                            {commitment.description}
                          </span>
                        </div>
                        <span className="font-medium text-blue-600">
                          {formatCurrency(commitment.installmentValue)}
                        </span>
                      </div>
                    ))}
                  </div>
                </div>

                {/* Next Month Details */}
                <div className="space-y-3">
                  <div className="flex items-center justify-between p-3 rounded-lg bg-purple-50 dark:bg-purple-900/20 border border-purple-200 dark:border-purple-800">
                    <div className="flex items-center gap-3">
                      <Clock className="w-5 h-5 text-purple-600" />
                      <div>
                        <h3 className="font-semibold text-gray-900 dark:text-white capitalize">{nextMonthName}</h3>
                        <p className="text-xs text-gray-600 dark:text-gray-400">{nextMonthCommitments.length} compromisso{nextMonthCommitments.length !== 1 ? 's' : ''}</p>
                      </div>
                    </div>
                    <p className="text-lg font-bold text-purple-600">
                      {formatCurrency(nextMonthTotal.toString())}
                    </p>
                  </div>

                  <div className="grid gap-2 ml-8">
                    {nextMonthCommitments.map((commitment, index) => (
                      <div
                        key={`next-${commitment.type}-${commitment.id}`}
                        className="flex items-center justify-between p-2 rounded border bg-white dark:bg-gray-800 text-sm"
                        data-testid={`next-commitment-${index}`}
                      >
                        <div className="flex items-center gap-2">
                          {getPaymentMethodIcon(commitment.paymentMethod, commitment.type)}
                          <span className="text-gray-900 dark:text-white truncate max-w-[200px]">
                            {commitment.description}
                          </span>
                        </div>
                        <span className="font-medium text-purple-600">
                          {formatCurrency(commitment.installmentValue)}
                        </span>
                      </div>
                    ))}
                  </div>
                </div>

                {/* Third Month Details */}
                <div className="space-y-3">
                  <div className="flex items-center justify-between p-3 rounded-lg bg-green-50 dark:bg-green-900/20 border border-green-200 dark:border-green-800">
                    <div className="flex items-center gap-3">
                      <Clock className="w-5 h-5 text-green-600" />
                      <div>
                        <h3 className="font-semibold text-gray-900 dark:text-white capitalize">{thirdMonthName}</h3>
                        <p className="text-xs text-gray-600 dark:text-gray-400">{thirdMonthCommitments.length} compromisso{thirdMonthCommitments.length !== 1 ? 's' : ''}</p>
                      </div>
                    </div>
                    <p className="text-lg font-bold text-green-600">
                      {formatCurrency(thirdMonthTotal.toString())}
                    </p>
                  </div>

                  <div className="grid gap-2 ml-8">
                    {thirdMonthCommitments.map((commitment, index) => (
                      <div
                        key={`third-${commitment.type}-${commitment.id}`}
                        className="flex items-center justify-between p-2 rounded border bg-white dark:bg-gray-800 text-sm"
                        data-testid={`third-commitment-${index}`}
                      >
                        <div className="flex items-center gap-2">
                          {getPaymentMethodIcon(commitment.paymentMethod, commitment.type)}
                          <span className="text-gray-900 dark:text-white truncate max-w-[200px]">
                            {commitment.description}
                          </span>
                        </div>
                        <span className="font-medium text-green-600">
                          {formatCurrency(commitment.installmentValue)}
                        </span>
                      </div>
                    ))}
                  </div>
                </div>
              </div>

              {/* Close Button */}
              <div className="flex justify-center mt-6 pt-4 border-t">
                <Button
                  onClick={() => setIsDialogOpen(false)}
                  variant="outline"
                  className="flex items-center gap-2"
                  data-testid="close-commitments-dialog"
                >
                  <X className="w-4 h-4" />
                  Fechar
                </Button>
              </div>
            </DialogContent>
          </Dialog>
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/dashboard/financia.content:
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Progress } from "@/components/ui/progress";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { useQuery } from "@tanstack/react-query";
import { Skeleton } from "@/components/ui/skeleton";
import { TrendingUp, Shield, AlertTriangle, CheckCircle, Target, ChevronLeft, ChevronRight } from "lucide-react";
import { useState } from "react";

interface FinancialHealthData {
  score: number;
  level: string;
  color: string;
  recommendations: string[];
  metrics: {
    incomeVsExpenses: {
      ratio: number;
      score: number;
    };
    emergencyFund: {
      months: number;
      score: number;
    };
    debtLevel: {
      ratio: number;
      score: number;
    };
  };
}

const getScoreColor = (score: number) => {
  if (score >= 80) return "text-green-600 dark:text-green-400";
  if (score >= 65) return "text-blue-600 dark:text-blue-400";
  if (score >= 45) return "text-yellow-600 dark:text-yellow-400";
  if (score >= 25) return "text-orange-600 dark:text-orange-400";
  return "text-red-600 dark:text-red-400";
};

const getProgressColor = (score: number) => {
  if (score >= 80) return "bg-green-500";
  if (score >= 65) return "bg-blue-500";
  if (score >= 45) return "bg-yellow-500";
  if (score >= 25) return "bg-orange-500";
  return "bg-red-500";
};

const getLevelIcon = (level: string) => {
  switch (level) {
    case 'Excelente':
      return <CheckCircle className="w-5 h-5 text-green-600 dark:text-green-400" />;
    case 'Bom':
      return <TrendingUp className="w-5 h-5 text-blue-600 dark:text-blue-400" />;
    case 'Regular':
      return <Target className="w-5 h-5 text-yellow-600 dark:text-yellow-400" />;
    case 'Baixo':
      return <AlertTriangle className="w-5 h-5 text-orange-600 dark:text-orange-400" />;
    default:
      return <AlertTriangle className="w-5 h-5 text-red-600 dark:text-red-400" />;
  }
};

export function FinancialHealthScore() {
  const [currentRecommendation, setCurrentRecommendation] = useState(0);

  const { data: healthData, isLoading } = useQuery<FinancialHealthData>({
    queryKey: ['/api/financial-health'],
    queryFn: async () => {
      const response = await fetch('/api/financial-health', {
        credentials: 'include',
      });
      if (!response.ok) throw new Error('Failed to fetch financial health score');
      return response.json();
    },
  });

  if (isLoading) {
    return (
      <Card className="financial-card">
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Shield className="w-5 h-5 text-blue-600" />
            Score de Sa√∫de Financeira
          </CardTitle>
        </CardHeader>
        <CardContent className="space-y-6">
          <div className="text-center">
            <Skeleton className="h-16 w-16 rounded-full mx-auto mb-4" />
            <Skeleton className="h-6 w-32 mx-auto mb-2" />
            <Skeleton className="h-8 w-full" />
          </div>
          <div className="space-y-4">
            {Array.from({ length: 3 }).map((_, i) => (
              <div key={i} className="space-y-2">
                <Skeleton className="h-4 w-24" />
                <Skeleton className="h-2 w-full" />
              </div>
            ))}
          </div>
        </CardContent>
      </Card>
    );
  }

  if (!healthData) {
    return (
      <Card className="financial-card">
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Shield className="w-5 h-5 text-blue-600" />
            Score de Sa√∫de Financeira
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="text-center py-8 text-gray-500 dark:text-gray-300">
            <p className="text-gray-600 dark:text-gray-300">Dados insuficientes para calcular o score.</p>
            <p className="text-sm text-gray-500 dark:text-gray-400">Adicione mais transa√ß√µes para obter uma an√°lise completa.</p>
          </div>
        </CardContent>
      </Card>
    );
  }

  const formatPercentage = (value: number) => {
    return `${Math.round(value * 100)}%`;
  };

  const formatMonths = (value: number) => {
    if (value < 1) return `${Math.round(value * 30)} dias`;
    return `${Math.round(value * 10) / 10} m√™s${value > 1.1 ? 'es' : ''}`;
  };

  return (
    <Card className="modern-card shadow-lg">
      <CardHeader className="pb-4">
        <CardTitle className="flex items-center gap-3 text-xl font-bold text-gray-900 dark:text-white">
          <Shield className="w-5 h-5 text-blue-600 dark:text-blue-400" />
          Score de Sa√∫de Financeira
        </CardTitle>
      </CardHeader>
      <CardContent className="space-y-6">
        {/* Main Score Display */}
        <div className="text-center">
          <div className="relative inline-flex items-center justify-center mb-4">
            <div className={`text-5xl font-bold tracking-tight ${getScoreColor(healthData.score)}`} data-testid="health-score">
              {healthData.score}
            </div>
            <span className="text-xl text-gray-500 dark:text-gray-300 ml-2 font-medium">/100</span>
          </div>
          <div className="flex items-center justify-center gap-3 mb-6">
            {getLevelIcon(healthData.level)}
            <Badge 
              variant="outline" 
              className={`${getScoreColor(healthData.score)} border-current px-3 py-1 text-sm font-semibold`}
              data-testid="health-level"
            >
              {healthData.level}
            </Badge>
          </div>
          <Progress 
            value={healthData.score} 
            className="w-full h-3"
            data-testid="health-progress"
          />
        </div>

        {/* Metrics Breakdown */}
        <div className="space-y-6">
          <h4 className="text-lg font-bold text-gray-900 dark:text-white">An√°lise Detalhada</h4>

          {/* Income vs Expenses */}
          <div className="space-y-2">
            <div className="flex justify-between items-center">
              <span className="text-sm text-gray-600 dark:text-gray-300">Taxa de Poupan√ßa</span>
              <span className="text-sm font-medium text-gray-900 dark:text-gray-100" data-testid="savings-rate">
                {formatPercentage(healthData.metrics.incomeVsExpenses.ratio)}
              </span>
