        </Button>
      </div>

      {/* Modal de sele√ß√£o de planos */}
      <PlanSelectionModal
        isOpen={showUpgradeModal}
        onClose={() => setShowUpgradeModal(false)}
        currentPlan={subscriptionStatus?.currentPlan}
      />
    </div>
  );
}

