import { useEffect, useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { 
  TrendingUp,
  TrendingDown,
  AlertTriangle,
  Lightbulb,
  RefreshCw,
  BarChart3,
  Target,
  Zap
} from 'lucide-react';
import { apiRequest } from '@/lib/queryClient';
import { useToast } from '@/hooks/use-toast';

interface SpendingPatternsData {
  insights: string[];
  warnings: string[];
  suggestions: string[];
}

export function SpendingPatterns() {
  const [patternsData, setPatternsData] = useState<SpendingPatternsData | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  const { toast } = useToast();

  const fetchPatterns = async () => {
    setIsLoading(true);
    try {
      const response = await apiRequest('GET', '/api/ai/spending-patterns');
      const data: SpendingPatternsData = await response.json();
      setPatternsData(data);
    } catch (error) {
      console.error('Erro ao obter padr√µes de gastos:', error);
      toast({
        title: "Erro",
        description: "N√£o foi poss√≠vel analisar seus padr√µes de gastos.",
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    fetchPatterns();
  }, []);

  if (isLoading) {
    return (
      <Card data-testid="spending-patterns-loading">
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <BarChart3 className="w-5 h-5" />
            An√°lise de Padr√µes
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="flex items-center justify-center py-8">
            <RefreshCw className="w-6 h-6 animate-spin text-slate-400" />
            <span className="ml-2 text-slate-600">Analisando seus padr√µes...</span>
          </div>
        </CardContent>
      </Card>
    );
  }

  if (!patternsData) {
    return (
      <Card data-testid="spending-patterns-error">
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <BarChart3 className="w-5 h-5" />
            An√°lise de Padr√µes
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="text-center py-8">
            <AlertTriangle className="w-12 h-12 text-orange-400 mx-auto mb-4" />
            <p className="text-slate-600 mb-4">N√£o foi poss√≠vel analisar os padr√µes</p>
            <Button onClick={fetchPatterns} variant="outline">
              Tentar Novamente
            </Button>
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <div className="space-y-6">
      {/* Insights */}
      {patternsData.insights && patternsData.insights.length > 0 && (
        <Card data-testid="spending-insights">
          <CardHeader>
            <CardTitle className="flex items-center gap-2 text-lg">
              <TrendingUp className="w-5 h-5 text-blue-600" />
              Insights Descobertos
              <Badge variant="secondary">{patternsData.insights.length}</Badge>
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-3">
              {patternsData.insights && patternsData.insights.map((insight, index) => (
                <div 
                  key={index}
                  className="flex items-start gap-3 p-3 bg-blue-50 rounded-lg border-l-4 border-blue-400"
                >
                  <TrendingUp className="w-4 h-4 text-blue-600 mt-0.5 flex-shrink-0" />
                  <p className="text-sm text-blue-900">{insight}</p>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>
      )}

      {/* Avisos */}
      {patternsData.warnings && patternsData.warnings.length > 0 && (
        <Card data-testid="spending-warnings">
          <CardHeader>
            <CardTitle className="flex items-center gap-2 text-lg">
              <AlertTriangle className="w-5 h-5 text-orange-600" />
              Pontos de Aten√ß√£o
              <Badge variant="destructive">{patternsData.warnings.length}</Badge>
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-3">
              {patternsData.warnings && patternsData.warnings.map((warning, index) => (
                <div 
                  key={index}
                  className="flex items-start gap-3 p-3 bg-orange-50 rounded-lg border-l-4 border-orange-400"
                >
                  <AlertTriangle className="w-4 h-4 text-orange-600 mt-0.5 flex-shrink-0" />
                  <p className="text-sm text-orange-900">{warning}</p>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>
      )}

      {/* Sugest√µes */}
      {patternsData.suggestions && patternsData.suggestions.length > 0 && (
        <Card data-testid="spending-suggestions">
          <CardHeader>
            <CardTitle className="flex items-center gap-2 text-lg">
              <Lightbulb className="w-5 h-5 text-green-600" />
              Sugest√µes de Melhoria
              <Badge variant="default">{patternsData.suggestions.length}</Badge>
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-3">
              {patternsData.suggestions && patternsData.suggestions.map((suggestion, index) => (
                <div 
                  key={index}
                  className="flex items-start gap-3 p-3 bg-green-50 rounded-lg border-l-4 border-green-400"
                >
                  <Target className="w-4 h-4 text-green-600 mt-0.5 flex-shrink-0" />
                  <p className="text-sm text-green-900">{suggestion}</p>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>
      )}

      {/* Estado vazio */}
      {(!patternsData.insights || patternsData.insights.length === 0) && 
       (!patternsData.warnings || patternsData.warnings.length === 0) && 
       (!patternsData.suggestions || patternsData.suggestions.length === 0) && (
        <Card data-testid="spending-patterns-empty">
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <BarChart3 className="w-5 h-5" />
              An√°lise de Padr√µes
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-center py-8">
              <Zap className="w-12 h-12 text-slate-300 mx-auto mb-4" />
              <p className="text-slate-600 mb-2">Ainda n√£o h√° dados suficientes</p>
              <p className="text-sm text-slate-500">
                Continue registrando suas transa√ß√µes para gerar insights personalizados
              </p>
            </div>
          </CardContent>
        </Card>
      )}

      {/* A√ß√µes */}
      <Card>
        <CardContent className="pt-6">
          <div className="flex items-center justify-between">
            <div>
              <p className="font-medium">An√°lise Atualizada</p>
              <p className="text-sm text-slate-600">
                A an√°lise √© baseada em suas transa√ß√µes mais recentes
              </p>
            </div>
            <Button 
              onClick={fetchPatterns} 
              variant="outline"
              data-testid="refresh-patterns"
            >
              <RefreshCw className="w-4 h-4 mr-2" />
              Atualizar
            </Button>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

