    { id: 'consultoria', name: 'Servi√ßos / Consultoria', icon: Users, color: 'bg-purple-500' },
    { id: 'contabil', name: 'Escrit√≥rio Cont√°bil / Jur√≠dico', icon: Calculator, color: 'bg-orange-500' },
