  const handlePriorityToggle = (priorityId: string) => {
    setWizardData(prev => ({
      ...prev,
      priorities: prev.priorities.includes(priorityId)
        ? prev.priorities.filter(p => p !== priorityId)
        : prev.priorities.length < 2 
          ? [...prev.priorities, priorityId]
