          </div>
          <div>
            <span className="text-gray-500">√öltima execu√ß√£o:</span>
            <span className="ml-2 font-medium">
              {rule.lastExecuted 
                ? new Date(rule.lastExecuted).toLocaleDateString('pt-BR')
                : 'Nunca'
              }
            </span>
          </div>
        </div>

        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-2">
            <Badge variant="outline" className="text-xs">
              {rule.type === 'transfer' ? 'Transfer√™ncia' :
               rule.type === 'investment' ? 'Investimento' :
               rule.type === 'alert' ? 'Alerta' :
               rule.type === 'categorization' ? 'Categoriza√ß√£o' : 'Outro'}
            </Badge>
            {rule.isRecurring && (
