    name: string;
    type: string;
    parameters: any;
  };
  results: {
    summary: {
      feasible: boolean;
      timeToGoal: number;
      monthlyRequired: number;
      totalCost: number;
      successProbability: number;
    };
    projections: Array<{
      month: number;
      balance: number;
      income: number;
      expenses: number;
      netFlow: number;
    }>;
    recommendations: string[];
    warnings: string[];
    milestones: Array<{
      month: number;
      description: string;
      target: number;
      achieved: number;
    }>;
  };
  confidence: number;
  createdAt: string;
}

const scenarioTemplates: ScenarioTemplate[] = [
  {
    id: 'retirement',
    name: 'Planejamento de Aposentadoria',
    description: 'Simule quando e como se aposentar com seguran√ßa financeira',
    type: 'retirement',
