      businessType: 'Telecomunica√ß√µes',
      description: 'Servi√ßos de telefonia e internet'
    };
  }

  // Energia El√©trica
  if (activity.includes('energia el√©trica') || activity.includes('distribui√ß√£o de energia') ||
      name.includes('energia') || name.includes('el√©trica') || name.includes('copel') ||
      name.includes('cemig') || name.includes('light') || name.includes('eletrobras')) {
