    };
  }

  // Lojas e Varejo
  if (activity.includes('com√©rcio varejista') || activity.includes('loja') || activity.includes('varejo') ||
      activity.includes('vestu√°rio') || activity.includes('cal√ßados') || activity.includes('magazine') ||
