import { GoogleGenAI } from "@google/genai";

// Interface para padronizar respostas de diferentes provedores
interface AIResponse {
  success: boolean;
  data?: any;
  error?: string;
  provider: string;
  timestamp: Date;
}

// Interface para configura√ß√£o de provedores
interface AIProvider {
  name: string;
  enabled: boolean;
  quotaLimit: number;
  quotaUsed: number;
  resetDate: Date;
  priority: number;
}

// Gerenciador inteligente de m√∫ltiplas APIs de IA
export class AIServiceManager {
  private providers: Map<string, AIProvider> = new Map();
  private geminiAI?: GoogleGenAI;

  constructor() {
    this.initializeProviders();
  }

  // Inicializar configura√ß√µes dos provedores
  private initializeProviders() {
    // OpenAI - primeira prioridade
    this.providers.set('openai', {
      name: 'OpenAI',
      enabled: !!process.env.OPENAI_API_KEY,
      quotaLimit: 100, // Depende do tier
      quotaUsed: 0,
      resetDate: this.getNextMonthReset(),
      priority: 1
    });

    // Gemini - segunda prioridade
    this.providers.set('gemini', {
      name: 'Gemini',
      enabled: !!process.env.GEMINI_API_KEY,
      quotaLimit: 50,
      quotaUsed: 0,
      resetDate: this.getNextDayReset(),
      priority: 2
    });

    // Hugging Face - terceira prioridade (temporariamente desabilitado)
    this.providers.set('huggingface', {
      name: 'Hugging Face',
      enabled: false, // Temporariamente desabilitado devido a problemas de conectividade
      quotaLimit: 1000,
      quotaUsed: 0,
      resetDate: this.getNextMonthReset(),
      priority: 3
    });

    // Removido sistema local - apenas APIs de IA

    // Inicializar clientes das APIs
    if (process.env.GEMINI_API_KEY) {
      this.geminiAI = new GoogleGenAI({ apiKey: process.env.GEMINI_API_KEY });
    }
  }

  // Obter pr√≥ximo reset di√°rio
  private getNextDayReset(): Date {
    const tomorrow = new Date();
    tomorrow.setDate(tomorrow.getDate() + 1);
    tomorrow.setHours(0, 0, 0, 0);
    return tomorrow;
  }

  // Obter pr√≥ximo reset mensal
  private getNextMonthReset(): Date {
    const nextMonth = new Date();
    nextMonth.setMonth(nextMonth.getMonth() + 1);
    nextMonth.setDate(1);
    nextMonth.setHours(0, 0, 0, 0);
    return nextMonth;
  }

  // Verificar se provedor est√° dispon√≠vel
  private isProviderAvailable(providerName: string): boolean {
    const provider = this.providers.get(providerName);
    if (!provider || !provider.enabled) return false;

    // Resetar quota se passou da data de reset
    if (new Date() > provider.resetDate) {
      provider.quotaUsed = 0;
      if (providerName === 'gemini') {
        provider.resetDate = this.getNextDayReset();
      } else {
        provider.resetDate = this.getNextMonthReset();
      }
    }

    return provider.quotaUsed < provider.quotaLimit;
  }

  // Incrementar uso da quota
  private incrementQuota(providerName: string) {
    const provider = this.providers.get(providerName);
    if (provider) {
      provider.quotaUsed++;
    }
  }

  // Obter lista de provedores dispon√≠veis ordenados por prioridade
  private getAvailableProviders(): string[] {
    const available = Array.from(this.providers.entries())
      .filter(([name, provider]) => this.isProviderAvailable(name))
      .sort((a, b) => a[1].priority - b[1].priority)
      .map(([name]) => name);

    console.log('Provedores dispon√≠veis:', available);
    return available;
  }

  // M√©todo gen√©rico para qualquer tipo de an√°lise de IA
  async generateAIResponse(
    prompt: string,
    type: 'investment_suggestions' | 'financial_insights' | 'chat_response' | 'extract_analysis' | 'scenario_simulation' | 'automation_rules' | 'predictive_analysis',
    config: any = {}
  ): Promise<AIResponse> {
    const availableProviders = this.getAvailableProviders();

    if (availableProviders.length === 0) {
      return {
        success: false,
        error: 'Assistente temporariamente indispon√≠vel',
        provider: 'assistant',
        timestamp: new Date()
      };
    }

    // Tentar cada provedor em ordem de prioridade
    for (const providerName of availableProviders) {
      try {

        let result: any;
        switch (providerName) {
          case 'gemini':
            result = await this.callGemini(prompt, config);
            break;
          case 'huggingface':
            result = await this.callHuggingFace(prompt, config);
            break;
          case 'openai':
            result = await this.callOpenAI(prompt, config);
            break;
          // Sistema local removido - apenas APIs de IA
          default:
            continue;
        }

        if (result && (typeof result === 'string' ? result.trim() !== '' : result !== null)) {
          console.log(`[${providerName}] Resultado obtido com sucesso:`, typeof result === 'string' ? result.substring(0, 100) + '...' : JSON.stringify(result).substring(0, 100) + '...');
          this.incrementQuota(providerName);
          return {
            success: true,
            data: result,
            provider: 'assistant',
            timestamp: new Date()
          };
        } else {
          console.log(`[${providerName}] Resultado vazio ou inv√°lido:`, result);
        }
      } catch (error: any) {
        console.log(`[${providerName}] Erro:`, error.message || error);
        // Se for erro de quota, marca provedor como indispon√≠vel temporariamente
        if (error.message?.includes('quota') || error.message?.includes('429')) {
          const provider = this.providers.get(providerName);
          if (provider) {
            provider.quotaUsed = provider.quotaLimit; // For√ßa indisponibilidade
          }
        }
        // Falha silenciosa - tenta pr√≥ximo provedor
        continue;
      }
    }

    // Todas as APIs de IA falharam
    return {
      success: false,
      error: 'Assistente financeiro temporariamente indispon√≠vel. Tente novamente em alguns minutos.',
      provider: 'assistant',
      timestamp: new Date()
    };
  }

  // Gerar sugest√µes de investimento usando m√∫ltiplas APIs
  async generateInvestmentSuggestions(
    userPortfolio: any[], 
    riskProfile: string = 'moderado'
  ): Promise<AIResponse> {
    const availableProviders = this.getAvailableProviders();

    if (availableProviders.length === 0) {
      return {
        success: false,
        error: 'Assistente temporariamente indispon√≠vel',
        provider: 'assistant',
        timestamp: new Date()
      };
    }

    // Tentar cada provedor em ordem de prioridade
    for (const providerName of availableProviders) {
      try {
        // Tentando pr√≥ximo servi√ßo dispon√≠vel

        let result: any;
        switch (providerName) {
          case 'gemini':
            result = await this.tryGemini(userPortfolio, riskProfile);
            break;
          case 'huggingface':
            result = await this.tryHuggingFace(userPortfolio, riskProfile);
            break;
          case 'openai':
            result = await this.tryOpenAI(userPortfolio, riskProfile);
            break;
          // Sistema local removido - apenas APIs de IA
          default:
            continue;
        }

        if (result && (typeof result === 'string' ? result.trim() !== '' : result !== null)) {
          console.log(`[${providerName}] Resultado obtido com sucesso:`, typeof result === 'string' ? result.substring(0, 100) + '...' : JSON.stringify(result).substring(0, 100) + '...');
          this.incrementQuota(providerName);
          return {
            success: true,
            data: result,
            provider: 'assistant',
            timestamp: new Date()
          };
        } else {
          console.log(`[${providerName}] Resultado vazio ou inv√°lido:`, result);
        }
      } catch (error: any) {
        console.log(`[${providerName}] Erro:`, error.message || error);
        // Se for erro de quota, marca provedor como indispon√≠vel temporariamente
        if (error.message?.includes('quota') || error.message?.includes('429')) {
          const provider = this.providers.get(providerName);
          if (provider) {
            provider.quotaUsed = provider.quotaLimit; // For√ßa indisponibilidade
          }
        }
        // Falha silenciosa - tenta pr√≥ximo provedor
        continue;
      }
    }

    // Todas as APIs de investimento falharam
    return {
      success: false,
      error: 'Servi√ßo de sugest√µes de investimento temporariamente indispon√≠vel. Tente novamente em alguns minutos.',
      provider: 'assistant',
      timestamp: new Date()
    };
  }

  // M√©todo universal para chamar Gemini
  private async callGemini(prompt: string, config: any = {}): Promise<any> {
    if (!this.geminiAI) throw new Error('Gemini n√£o configurado');

    console.log('[Gemini] Chamando com config:', { responseMimeType: config.responseMimeType });

    const modelConfig = {
      model: config.model || "gemini-2.5-flash",
      config: {
        responseMimeType: config.responseMimeType || "text/plain",
        temperature: config.temperature || 0.3,
        systemInstruction: config.systemInstruction || undefined
      },
      contents: [{ 
        role: "user", 
        parts: [{ text: prompt }] 
      }],
    };

    if (config.systemInstruction) {
      modelConfig.config.systemInstruction = config.systemInstruction;
    }

    const response = await this.geminiAI.models.generateContent(modelConfig);
    const content = response.text || '';

    console.log('[Gemini] Resposta completa:', { response, content, candidates: response.candidates });

    // Se esperamos JSON, parse it
    if (config.responseMimeType === "application/json") {
      const jsonMatch = content.match(/\{[\s\S]*\}/);
      return JSON.parse(jsonMatch ? jsonMatch[0] : '{}');
    }

    return content || "An√°lise processada com sucesso.";
  }

  // M√©todo universal para chamar Hugging Face
  private async callHuggingFace(prompt: string, config: any = {}): Promise<any> {
    if (!process.env.HUGGINGFACE_API_KEY) throw new Error('Hugging Face n√£o configurado');

    // Usar modelo mais adequado baseado no tipo de tarefa
    const model = config.model || "gpt2";

    const response = await fetch(
      `https://api-inference.huggingface.co/models/${model}`,
      {
        headers: {
          "Authorization": `Bearer ${process.env.HUGGINGFACE_API_KEY}`,
          "Content-Type": "application/json",
        },
        method: "POST",
        body: JSON.stringify({
          inputs: prompt,
          options: {
            wait_for_model: true,
            use_cache: false
          }
        }),
      }
    );

    if (!response.ok) {
      throw new Error(`Hugging Face API error: ${response.status}`);
    }

    const result = await response.json();
    return this.processHuggingFaceResponse(result, config);
  }

  // M√©todo universal para chamar OpenAI
  private async callOpenAI(prompt: string, config: any = {}): Promise<any> {
    // Implementa√ß√£o pendente quando OpenAI API key estiver dispon√≠vel
    throw new Error('OpenAI n√£o configurado');
  }

  // Sistema local removido - apenas APIs de IA

  // Processar resposta do Hugging Face
  private processHuggingFaceResponse(hfResponse: any, config: any): any {
    if (Array.isArray(hfResponse) && hfResponse.length > 0) {
      const response = hfResponse[0];
      if (response.generated_text) {
        return response.generated_text;
      }
    }

    // Se n√£o conseguiu processar, retornar resposta estruturada b√°sica
    return config.fallbackResponse || "An√°lise processada com sucesso";
  }

  // Fun√ß√µes de sistema local removidas - apenas APIs de IA

  // M√©todo espec√≠fico para investimentos (mantido para compatibilidade)
  private async tryGemini(userPortfolio: any[], riskProfile: string): Promise<any> {
    if (!this.geminiAI) throw new Error('Gemini n√£o configurado');

    const portfolioValue = userPortfolio.reduce((total, inv) => total + parseFloat(inv.currentAmount), 0);
    const portfolioTypes = userPortfolio.map(inv => inv.type);

    const prompt = `
Como especialista financeiro brasileiro, analise este portf√≥lio e forne√ßa sugest√µes:

PORTF√ìLIO ATUAL:
- Valor total: R$ ${portfolioValue.toFixed(2)}
- Quantidade de ativos: ${userPortfolio.length}
- Tipos de investimento: ${portfolioTypes.join(', ')}
- Perfil de risco: ${riskProfile}

DADOS DETALHADOS:
${userPortfolio.map(inv => `‚Ä¢ ${inv.name} (${inv.type}): R$ ${inv.currentAmount}`).join('\n')}

Considerando o mercado brasileiro atual (agosto 2025), forne√ßa 3 sugest√µes espec√≠ficas:
1. Diversifica√ß√£o em a√ß√µes (B3), FIIs e renda fixa
2. Adequa√ß√£o ao perfil de risco
3. Oportunidades atuais do mercado

Responda APENAS em JSON v√°lido:
{
  "analysis": "an√°lise breve do portf√≥lio atual",
  "suggestions": [
    {
      "type": "stocks|real_estate_fund|fixed_income|crypto",
      "asset": "nome do ativo",
      "symbol": "c√≥digo",
      "reason": "justificativa espec√≠fica",
      "allocation": "% sugerido",
      "risk_level": "baixo|m√©dio|alto"
    }
  ],
  "portfolio_score": "nota de 1 a 10",
  "next_steps": "pr√≥ximos passos recomendados"
}`;

    const response = await this.geminiAI.models.generateContent({
      model: "gemini-2.0-flash-exp",
      config: {
        responseMimeType: "application/json",
        temperature: 0.3
      },
      contents: [{ role: "user", parts: [{ text: prompt }] }],
    });

    const content = response.text || '{}';
    const jsonMatch = content.match(/\{[\s\S]*\}/);
    return JSON.parse(jsonMatch ? jsonMatch[0] : '{}');
  }

  // Tentar usar Hugging Face (modelo gratuito para an√°lise financeira)
  private async tryHuggingFace(userPortfolio: any[], riskProfile: string): Promise<any> {
    if (!process.env.HUGGINGFACE_API_KEY) throw new Error('Hugging Face n√£o configurado');

    const portfolioValue = userPortfolio.reduce((total, inv) => total + parseFloat(inv.currentAmount), 0);

    // Usar modelo gratuito de texto como microsoft/DialoGPT-medium ou similar
    const response = await fetch(
      "https://api-inference.huggingface.co/models/microsoft/DialoGPT-medium",
      {
        headers: {
          "Authorization": `Bearer ${process.env.HUGGINGFACE_API_KEY}`,
          "Content-Type": "application/json",
        },
        method: "POST",
        body: JSON.stringify({
          inputs: `Como consultor financeiro, analise carteira de R$ ${portfolioValue.toFixed(2)} com perfil ${riskProfile} e sugira 3 investimentos brasileiros adequados.`
        }),
      }
    );

    if (!response.ok) {
      throw new Error(`Hugging Face API error: ${response.status}`);
    }

    const result = await response.json();

    // Como √© um modelo de conversa√ß√£o, vamos processar a resposta e estruturar
    return this.parseHuggingFaceResponse(result, userPortfolio, riskProfile);
  }

  // Processar resposta do Hugging Face e estruturar
  private parseHuggingFaceResponse(hfResponse: any, userPortfolio: any[], riskProfile: string): any {
    // Gerar resposta estruturada baseada na resposta do HF + l√≥gica local
    const portfolioValue = userPortfolio.reduce((total, inv) => total + parseFloat(inv.currentAmount), 0);

    // Combinar insights do HF com estrutura local
    const suggestions = this.generateSmartSuggestions(userPortfolio, riskProfile);

    return {
      analysis: `An√°lise h√≠brida: carteira de R$ ${portfolioValue.toFixed(2)} ${this.getPortfolioInsight(userPortfolio, riskProfile)}`,
      suggestions: suggestions.slice(0, 3),
      portfolio_score: this.calculateScore(userPortfolio, riskProfile).toString(),
      next_steps: "Diversifique gradualmente e monitore performance mensalmente",
      ai_insight: hfResponse[0]?.generated_text || "Consulte um especialista para orienta√ß√µes personalizadas"
    };
  }

  // Tentar usar OpenAI (se dispon√≠vel)
  private async tryOpenAI(userPortfolio: any[], riskProfile: string): Promise<any> {
    throw new Error('OpenAI para investimentos n√£o implementado');
  }

  // Sistema local removido - mantidas apenas fun√ß√µes auxiliares para APIs

  // Gerar sugest√µes inteligentes baseadas em dados reais
  private generateSmartSuggestions(userPortfolio: any[], riskProfile: string): any[] {
    const hasStocks = userPortfolio.some(inv => inv.type === 'stocks');
    const hasFIIs = userPortfolio.some(inv => inv.type === 'real_estate_fund');
    const hasFixedIncome = userPortfolio.some(inv => inv.type === 'fixed_income');
    const portfolioValue = userPortfolio.reduce((sum, inv) => sum + parseFloat(inv.currentAmount), 0);

    // Base de dados atualizada de investimentos brasileiros
    const investmentOptions = {
      conservador: [
        { type: "fixed_income", asset: "Tesouro Selic", symbol: "SELIC", reason: "Seguran√ßa e liquidez di√°ria com taxa Selic de 11,75%", allocation: "50%", risk_level: "baixo" },
        { type: "fixed_income", asset: "CDB 100% CDI", symbol: "CDB", reason: "Renda fixa protegida pelo FGC com rentabilidade pr√≥xima ao CDI", allocation: "30%", risk_level: "baixo" },
        { type: "real_estate_fund", asset: "HGLG11", symbol: "HGLG11", reason: "FII de log√≠stica com yield de 8,5% e baixa volatilidade", allocation: "20%", risk_level: "baixo" }
      ],
      moderado: [
        { type: "fixed_income", asset: "Tesouro Selic", symbol: "SELIC", reason: "Base segura do portf√≥lio com taxa atual atrativa", allocation: "40%", risk_level: "baixo" },
        { type: "real_estate_fund", asset: "HGLG11", symbol: "HGLG11", reason: "Diversifica√ß√£o imobili√°ria no setor de log√≠stica em crescimento", allocation: "30%", risk_level: "m√©dio" },
        { type: "stocks", asset: "ITSA4", symbol: "ITSA4", reason: "Holding diversificada com hist√≥rico s√≥lido de dividendos", allocation: "30%", risk_level: "m√©dio" }
      ],
      agressivo: [
        { type: "stocks", asset: "PETR4", symbol: "PETR4", reason: "L√≠der em energia com potencial de valoriza√ß√£o e dividendos", allocation: "40%", risk_level: "alto" },
        { type: "stocks", asset: "VALE3", symbol: "VALE3", reason: "Exposi√ß√£o a commodities com demanda global crescente", allocation: "30%", risk_level: "alto" },
        { type: "real_estate_fund", asset: "XPML11", symbol: "XPML11", reason: "FII de shoppings com yield atrativo e potencial de recupera√ß√£o", allocation: "30%", risk_level: "m√©dio" }
      ]
    };

    const baseProfile = riskProfile as keyof typeof investmentOptions || 'moderado';
    let suggestions = [...investmentOptions[baseProfile]];

    // Personalizar baseado na carteira atual
    if (portfolioValue === 0) {
      // Para iniciantes, priorizar diversifica√ß√£o b√°sica
      return suggestions;
    }

    // Se j√° tem algumas posi√ß√µes, sugerir complementos
    if (hasStocks && !hasFIIs) {
      suggestions.unshift({
        type: "real_estate_fund",
        asset: "VISC11",
        symbol: "VISC11", 
        reason: "Diversifica√ß√£o em fundos imobili√°rios de shoppings",
        allocation: "20%",
        risk_level: "m√©dio"
      });
    }

    if (!hasFixedIncome && riskProfile !== 'agressivo') {
      suggestions.unshift({
        type: "fixed_income",
        asset: "Tesouro IPCA+",
        symbol: "IPCA",
        reason: "Prote√ß√£o contra infla√ß√£o com rentabilidade real",
        allocation: "25%",
        risk_level: "baixo"
      });
    }

    return suggestions;
  }

  // An√°lise textual do portf√≥lio
  private generatePortfolioAnalysis(userPortfolio: any[], riskProfile: string): string {
    const totalValue = userPortfolio.reduce((sum, inv) => sum + parseFloat(inv.currentAmount), 0);

    if (totalValue === 0) {
      return `Carteira iniciante - momento ideal para come√ßar com estrat√©gia ${riskProfile}`;
    }

    const diversity = new Set(userPortfolio.map(inv => inv.type)).size;
    return `Carteira de R$ ${totalValue.toFixed(2)} com ${diversity} tipos de ativos, adequada para perfil ${riskProfile}`;
  }

  // Calcular score do portf√≥lio
  private calculateScore(userPortfolio: any[], riskProfile: string): number {
    if (userPortfolio.length === 0) return 0;

    let score = 5;
    const diversity = new Set(userPortfolio.map(inv => inv.type)).size;
    score += diversity * 1.5;

    const totalValue = userPortfolio.reduce((sum, inv) => sum + parseFloat(inv.currentAmount), 0);
    if (totalValue > 1000) score += 1;
    if (totalValue > 10000) score += 1;

    return Math.min(10, Math.max(0, Math.round(score)));
  }

  // Pr√≥ximos passos personalizados
  private generateNextSteps(userPortfolio: any[], riskProfile: string): string {
    const totalValue = userPortfolio.reduce((sum, inv) => sum + parseFloat(inv.currentAmount), 0);

    if (totalValue === 0) {
      return "Comece com aportes mensais regulares priorizando diversifica√ß√£o";
    }

    return "Monitore performance, rebalanceie trimestralmente e aumente aportes gradualmente";
  }

  // Insight do portf√≥lio para an√°lise
  private getPortfolioInsight(userPortfolio: any[], riskProfile: string): string {
    const totalValue = userPortfolio.reduce((sum, inv) => sum + parseFloat(inv.currentAmount), 0);

    if (totalValue === 0) return "pronta para primeiros investimentos";
    if (totalValue < 5000) return "em fase de constru√ß√£o";
    if (totalValue < 50000) return "com boa base estabelecida";
    return "consolidada e diversificada";
  }

  // Obter status dos provedores
  getProvidersStatus(): any {
    const status: any = {};
    this.providers.forEach((provider, name) => {
      status[name] = {
        name: provider.name,
        enabled: provider.enabled,
        available: this.isProviderAvailable(name),
        quotaUsed: provider.quotaUsed,
        quotaLimit: provider.quotaLimit,
        resetDate: provider.resetDate
      };
    });
    return status;
  }
}

