/**
 * DeepSeek AI Categorization Service
 * Servi√ßo dedicado exclusivamente para categoriza√ß√£o inteligente de transa√ß√µes
 */

interface Transaction {
  description: string;
  amount: number;
  type: 'income' | 'expense';
  date: string;
}

interface CategorizedTransaction {
  date: string;
  description: string;
  amount: number;
  type: 'income' | 'expense';
  category: string;
  confidence: number;
  reasoning?: string;
}

export class DeepSeekCategorizationService {
  private apiKey: string;
  private baseUrl = 'https://api.deepseek.com/v1/chat/completions';

  constructor() {
    this.apiKey = process.env.DEEPSEEK_API_KEY || '';
    if (!this.apiKey) {
      console.warn('DEEPSEEK_API_KEY n√£o configurada - categoriza√ß√£o DeepSeek desabilitada');
    }
  }

  /**
   * NOVA FUN√á√ÉO: Extrai e categoriza transa√ß√µes diretamente do texto do extrato
   * COM PROCESSAMENTO EM CHUNKS para evitar timeout
   */
  async extractAndCategorizeTransactions(extractText: string): Promise<CategorizedTransaction[]> {
    if (!this.apiKey) {
      console.log('[DeepSeek] API key n√£o dispon√≠vel');
      return [];
    }

    try {
      console.log(`[DeepSeek] Iniciando extra√ß√£o de ${extractText.length} caracteres`);

      // Para textos grandes, dividir em chunks
      if (extractText.length > 10000) {
        console.log(`[DeepSeek] Texto grande (${extractText.length}), processando em chunks`);
        return await this.processInChunks(extractText);
      }

      // Para textos pequenos, processar direto
      const prompt = this.buildExtractionPrompt(extractText);
      const response = await this.callDeepSeekAPI(prompt);

      const transactions = this.parseExtractionResponse(response);

      console.log(`[DeepSeek] Extra√ß√£o conclu√≠da: ${transactions.length} transa√ß√µes`);
      return transactions;

    } catch (error) {
      console.error('[DeepSeek] Erro na extra√ß√£o:', error);
      return [];
    }
  }

  /**
   * Processa texto grande em chunks menores
   */
  private async processInChunks(extractText: string): Promise<CategorizedTransaction[]> {
    const chunkSize = 8000; // Chunks maiores para menos requisi√ß√µes
    const chunks: string[] = [];

    // Dividir por linhas para n√£o cortar transa√ß√µes
    const lines = extractText.split('\n');
    let currentChunk = '';

    for (const line of lines) {
      if (currentChunk.length + line.length > chunkSize && currentChunk.length > 0) {
        chunks.push(currentChunk.trim());
        currentChunk = line;
      } else {
        currentChunk += (currentChunk ? '\n' : '') + line;
      }
    }

    if (currentChunk.trim()) {
      chunks.push(currentChunk.trim());
    }

    console.log(`[DeepSeek] Dividido em ${chunks.length} chunks de ~${chunkSize} caracteres`);

    // Processar chunks em paralelo para maior velocidade
    const chunkPromises = chunks.map(async (chunk, index) => {
      try {
        console.log(`[DeepSeek] Processando chunk ${index + 1}/${chunks.length} (${chunk.length} chars)`);

        const prompt = this.buildExtractionPrompt(chunk);
        const response = await this.callDeepSeekAPI(prompt);
        const chunkTransactions = this.parseExtractionResponse(response);

        console.log(`[DeepSeek] Chunk ${index + 1}: ${chunkTransactions.length} transa√ß√µes encontradas`);
        return chunkTransactions;

      } catch (error) {
        console.error(`[DeepSeek] Erro no chunk ${index + 1}:`, error);
        return [];
      }
    });

    // Aguardar todos os chunks processarem em paralelo
    const allChunkResults = await Promise.all(chunkPromises);
    const allTransactions = allChunkResults.flat();

    console.log(`[DeepSeek] Processamento em chunks conclu√≠do: ${allTransactions.length} transa√ß√µes total`);
    return allTransactions;
  }

  /**
   * Categoriza uma lista de transa√ß√µes usando DeepSeek
   */
  async categorizeTransactions(transactions: Transaction[]): Promise<CategorizedTransaction[]> {
    if (!this.apiKey) {
      console.log('[DeepSeek] API key n√£o dispon√≠vel, usando categoriza√ß√£o padr√£o');
      return this.fallbackCategorization(transactions);
    }

    try {
      console.log(`[DeepSeek] Iniciando categoriza√ß√£o de ${transactions.length} transa√ß√µes`);

      const prompt = this.buildCategorizationPrompt(transactions);
      const response = await this.callDeepSeekAPI(prompt);

      const categorizedTransactions = this.parseCategorizationResponse(response, transactions);

      console.log(`[DeepSeek] Categoriza√ß√£o conclu√≠da: ${categorizedTransactions.length} transa√ß√µes categorizadas`);
      return categorizedTransactions;

    } catch (error) {
      console.error('[DeepSeek] Erro na categoriza√ß√£o:', error);
      return this.fallbackCategorization(transactions);
    }
  }

  /**
   * Constr√≥i o prompt OTIMIZADO para extra√ß√£o r√°pida
   */
  private buildExtractionPrompt(extractText: string): string {
    return `Voc√™ √© um especialista em extrair transa√ß√µes de extratos banc√°rios brasileiros.

INSTRU√á√ïES CR√çTICAS:
1. EXTRAIA APENAS transa√ß√µes financeiras reais (com data, valor e descri√ß√£o)
2. IGNORE cabe√ßalhos, rodap√©s, saldos, informa√ß√µes do banco, propagandas
3. CLASSIFIQUE corretamente como income (receita) ou expense (despesa)

REGRAS PARA TIPO DE TRANSA√á√ÉO:
INCOME (receitas):
- PIX recebido, TED recebido, DOC recebido
- Dep√≥sitos, cr√©ditos em conta
- Sal√°rios, pagamentos recebidos
- Estornos, reembolsos
- Transfer√™ncias recebidas

EXPENSE (despesas):
- PIX enviado, TED enviado, DOC enviado
- Compras (cart√£o, d√©bito)
- Pagamentos, boletos
- Saques
- Tarifas banc√°rias
- Transfer√™ncias enviadas

CATEGORIAS ESPEC√çFICAS:
- Alimenta√ß√£o: supermercados (EXTRA, CARREFOUR, P√ÉO DE A√á√öCAR), restaurantes, delivery (IFOOD, UBER EATS)
- Transporte: combust√≠vel (POSTO, SHELL, BR), Uber, 99, metr√¥, ped√°gio
- Casa: aluguel, condom√≠nio, energia (CEMIG, CPFL), √°gua (SABESP), internet (VIVO, CLARO)
- Sa√∫de: farm√°cias (DROGA RAIA, PAGUE MENOS), hospitais, laborat√≥rios
- Educa√ß√£o: escolas, cursos, livros
- Entretenimento: cinema, streaming (NETFLIX, SPOTIFY), viagens
- Vestu√°rio: lojas de roupas, cal√ßados
- Servi√ßos: sal√£o, barbeiro, consertos
- Investimentos: corretoras, fundos, poupan√ßa
- Outros: se n√£o se encaixar em nenhuma categoria

TEXTO DO EXTRATO:
${extractText}

IMPORTANTE: Se encontrar menos de 3 transa√ß√µes v√°lidas, retorne array vazio.

RESPONDA APENAS JSON V√ÅLIDO:
{"transactions":[{"date":"AAAA-MM-DD","description":"DESC","amount":VALOR,"type":"income/expense","category":"CATEGORIA","confidence":0.9}]}`;
  }

  /**
   * Processa a resposta da extra√ß√£o e retorna transa√ß√µes - COM PARSING ROBUSTO
   */
  private parseExtractionResponse(response: string): CategorizedTransaction[] {
    try {
      console.log(`[DeepSeek] Processando resposta: ${response.length} caracteres`);

      // Tentar m√∫ltiplas estrat√©gias de parsing
      let parsed;

      // Estrat√©gia 1: JSON limpo
      const jsonMatch = response.match(/\{[\s\S]*\}/);
      if (jsonMatch) {
        try {
          parsed = JSON.parse(jsonMatch[0]);
        } catch (e) {
          console.log(`[DeepSeek] JSON parsing falhou, tentando limpeza...`);

          // Estrat√©gia 2: Limpar JSON malformado
          let cleanedJson = jsonMatch[0]
            .replace(/,\s*}/g, '}')  // Remove v√≠rgulas extras antes de }
            .replace(/,\s*]/g, ']')  // Remove v√≠rgulas extras antes de ]
            .replace(/([{,]\s*)(\w+):/g, '$1"$2":'); // Adiciona aspas em chaves

          try {
            parsed = JSON.parse(cleanedJson);
          } catch (e2) {
            console.log(`[DeepSeek] Limpeza falhou, extraindo transa√ß√µes com regex...`);
            return this.extractWithRegex(response);
          }
        }
      } else {
        console.log(`[DeepSeek] Nenhum JSON encontrado, usando regex...`);
        return this.extractWithRegex(response);
      }

      const transactions = parsed.transactions || [];
      console.log(`[DeepSeek] ${transactions.length} transa√ß√µes encontradas no JSON`);

      const validTransactions = transactions
        .map((t: any) => ({
          date: t.date || new Date().toISOString().split('T')[0],
          description: t.description || 'Transa√ß√£o sem descri√ß√£o',
          amount: parseFloat(t.amount) || 0,
          type: (t.type || 'expense').toLowerCase(),
          category: t.category || 'Outros',
          confidence: t.confidence || 0.8,
          reasoning: t.reasoning || 'Categoriza√ß√£o autom√°tica'
        }))
        .filter(this.isValidTransaction);

      console.log(`[DeepSeek] ${validTransactions.length}/${transactions.length} transa√ß√µes v√°lidas ap√≥s filtros`);
      return validTransactions;

    } catch (error) {
      console.error('[DeepSeek] Erro ao processar resposta:', error);
      return this.extractWithRegex(response);
    }
  }

  /**
   * Valida se uma transa√ß√£o √© v√°lida e n√£o √© ru√≠do
   */
  private isValidTransaction(transaction: CategorizedTransaction): boolean {
    // Verificar se o valor √© v√°lido (maior que R$ 0,01)
    if (!transaction.amount || transaction.amount < 0.01) {
      return false;
    }

    // Verificar se a descri√ß√£o n√£o √© muito gen√©rica ou vazia
    const description = transaction.description.toLowerCase().trim();
    const invalidDescriptions = [
      'transa√ß√£o sem descri√ß√£o',
      'sem descri√ß√£o',
      'desc',
      'saldo',
      'saldo anterior',
      'saldo atual',
      'extrato',
      'conta corrente',
      'ag√™ncia',
      'banco',
      'cpf',
      'cnpj',
      'p√°gina',
      'per√≠odo',
      'total',
      'limite'
    ];

    if (description.length < 3 || invalidDescriptions.some(inv => description.includes(inv))) {
      return false;
    }

    // Verificar se a data √© v√°lida (√∫ltimos 2 anos)
    const transactionDate = new Date(transaction.date);
    const twoYearsAgo = new Date();
    twoYearsAgo.setFullYear(twoYearsAgo.getFullYear() - 2);
    const tomorrow = new Date();
    tomorrow.setDate(tomorrow.getDate() + 1);

    if (isNaN(transactionDate.getTime()) || 
        transactionDate < twoYearsAgo || 
        transactionDate > tomorrow) {
      return false;
    }

    // Verificar se o tipo √© v√°lido
    if (!['income', 'expense'].includes(transaction.type)) {
      return false;
    }

    return true;
  }

  /**
   * Extra√ß√£o via regex como fallback
   */
  private extractWithRegex(text: string): CategorizedTransaction[] {
    console.log(`[DeepSeek] Usando extra√ß√£o regex como fallback...`);

    const transactions: CategorizedTransaction[] = [];
    const lines = text.split('\n');

    for (const line of lines) {
      // Padr√µes b√°sicos de transa√ß√£o banc√°ria
      const patterns = [
        /(\d{2}\/\d{2}\/\d{4}).*?([A-Z\s]+).*?R?\$?\s*([\d,.-]+)/i,
        /(\d{4}-\d{2}-\d{2}).*?(PIX|TED|DOC|D√âBITO|CR√âDITO).*?([\d,.-]+)/i
      ];

      for (const pattern of patterns) {
        const match = line.match(pattern);
        if (match) {
          const amount = parseFloat(match[3].replace(/[^\d,-]/g, '').replace(',', '.'));
          if (!isNaN(amount) && amount !== 0) {
            transactions.push({
              date: this.normalizeDate(match[1]),
              description: match[2].trim(),
              amount: amount,
              type: amount > 0 ? 'income' : 'expense',
              category: 'Outros',
              confidence: 0.6,
              reasoning: 'Extra√ß√£o via regex'
            });
          }
          break;
        }
      }
    }

    const validTransactions = transactions.filter(this.isValidTransaction);
    console.log(`[DeepSeek] Regex extraiu ${validTransactions.length}/${transactions.length} transa√ß√µes v√°lidas`);
    return validTransactions;
  }

  /**
   * Normaliza formato de data
   */
  private normalizeDate(dateStr: string): string {
    try {
      if (dateStr.includes('/')) {
        const [day, month, year] = dateStr.split('/');
        return `${year}-${month.padStart(2, '0')}-${day.padStart(2, '0')}`;
      }
      return dateStr;
    } catch {
      return new Date().toISOString().split('T')[0];
    }
  }

  /**
   * Constr√≥i o prompt espec√≠fico para categoriza√ß√£o
   */
  private buildCategorizationPrompt(transactions: Transaction[]): string {
    const transactionsList = transactions.map((t, index) => 
      `${index + 1}. "${t.description}" - R$ ${t.amount} (${t.type}) - ${t.date}`
    ).join('\n');

    return `Voc√™ √© um especialista em categoriza√ß√£o de transa√ß√µes banc√°rias brasileiras.

REGRAS DE CATEGORIZA√á√ÉO:
- Alimenta√ß√£o: supermercados, restaurantes, delivery, padarias
- Transporte: combust√≠vel, Uber, 99, ped√°gios, estacionamento
- Casa: aluguel, condom√≠nio, energia, √°gua, internet, m√≥veis
- Sa√∫de: farm√°cias, hospitais, consultas, planos de sa√∫de
- Educa√ß√£o: escolas, cursos, livros, material escolar
- Entretenimento: cinema, streaming, jogos, viagens
- Vestu√°rio: roupas, cal√ßados, acess√≥rios
- Servi√ßos: sal√£o, barbeiro, consertos, limpeza
- Assinaturas: Netflix, Spotify, software, academias
- Investimentos: aplica√ß√µes, a√ß√µes, fundos
- Outros: transa√ß√µes que n√£o se encaixam nas categorias acima

INSTRU√á√ïES:
1. Analise cada transa√ß√£o considerando a descri√ß√£o e valor
2. Identifique estabelecimentos conhecidos (ex: MAGAZINELUIZA ‚Üí Outros/Eletr√¥nicos)
3. Para PIX, tente inferir pela descri√ß√£o ou valor
4. Retorne APENAS JSON v√°lido no formato especificado

TRANSA√á√ïES PARA CATEGORIZAR:
${transactionsList}

RESPONDA APENAS COM JSON:
{
  "transactions": [
    {
      "id": 1,
      "category": "categoria_escolhida",
      "confidence": 0.95,
      "reasoning": "motivo_da_categorizacao"
    }
  ]
}`;
  }

  /**
   * Chama a API do DeepSeek
   */
  private async callDeepSeekAPI(prompt: string): Promise<any> {
    const controller = new AbortController();
    const timeoutId = setTimeout(() => controller.abort(), 8000); // Timeout mais r√°pido - 8s

    try {
      const response = await fetch(this.baseUrl, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${this.apiKey}`,
        },
        body: JSON.stringify({
          model: 'deepseek-chat',
          messages: [
            {
              role: 'user',
              content: prompt
            }
          ],
          temperature: 0.1,
          max_tokens: 2000, // Reduzido para respostas mais r√°pidas
        }),
        signal: controller.signal,
      });

      clearTimeout(timeoutId);

      if (!response.ok) {
        throw new Error(`DeepSeek API error: ${response.status} ${response.statusText}`);
      }

      const data = await response.json();
      return data.choices[0].message.content;

    } catch (error) {
      clearTimeout(timeoutId);
      throw error;
    }
  }

  /**
   * Processa a resposta da IA e aplica √†s transa√ß√µes
   */
  private parseCategorizationResponse(response: string, originalTransactions: Transaction[]): CategorizedTransaction[] {
    try {
      const jsonMatch = response.match(/\{[\s\S]*\}/);
      if (!jsonMatch) {
        throw new Error('Resposta n√£o cont√©m JSON v√°lido');
      }

      const parsed = JSON.parse(jsonMatch[0]);
      const categorizations = parsed.transactions || [];

      return originalTransactions.map((transaction, index) => {
        const categorization = categorizations.find((c: any) => c.id === index + 1);

        return {
          ...transaction,
          category: categorization?.category || 'Outros',
          confidence: categorization?.confidence || 0.5,
          reasoning: categorization?.reasoning,
        };
      });

    } catch (error) {
      console.error('[DeepSeek] Erro ao processar resposta:', error);
      return this.fallbackCategorization(originalTransactions);
    }
  }

  /**
   * Categoriza√ß√£o de fallback usando regras simples
   */
  private fallbackCategorization(transactions: Transaction[]): CategorizedTransaction[] {
    return transactions.map(transaction => ({
      ...transaction,
      category: this.getSimpleCategory(transaction.description),
      confidence: 0.7,
      reasoning: 'Categoriza√ß√£o autom√°tica por palavras-chave',
    }));
  }

  /**
   * Categoriza√ß√£o simples baseada em palavras-chave
   */
  private getSimpleCategory(description: string): string {
    const desc = description.toLowerCase();

    if (desc.includes('superm') || desc.includes('mercado') || desc.includes('carrefour') || desc.includes('extra')) {
      return 'Alimenta√ß√£o';
    }
    if (desc.includes('posto') || desc.includes('combustivel') || desc.includes('shell') || desc.includes('ipiranga')) {
      return 'Transporte';
    }
    if (desc.includes('farmacia') || desc.includes('droga') || desc.includes('pague menos')) {
      return 'Sa√∫de';
    }
    if (desc.includes('netflix') || desc.includes('spotify') || desc.includes('disney') || desc.includes('amazon prime')) {
      return 'Assinaturas';
    }
    if (desc.includes('magazine') || desc.includes('americanas') || desc.includes('mercado livre')) {
      return 'Outros';
    }

    return 'Outros';
  }

  /**
   * Verifica se o servi√ßo est√° dispon√≠vel
   */
  isAvailable(): boolean {
    return !!this.apiKey;
  }
}

